import React, { useState } from 'react';
import styled from 'styled-components';
import { FiInfo } from 'react-icons/fi';

const PageWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 40px;
  min-height: 100vh;
  background-color: #D6C0B3; /* Beige background */
  font-family: 'Raleway', sans-serif;
`;

const Form = styled.form`
  width: 100%;
  max-width: 780px;
  padding: 30px;
  border-radius: 10px;
  background: transparent;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1);
  border: 1px solid rgba(200, 200, 200, 0.5);
`;

const FormGroup = styled.div`
  margin-bottom: 20px;
  display: flex;
  flex-direction: column;
  position: relative;
`;

const Title = styled.h2`
  text-align: center;
  margin-bottom: 30px;
  font-family: 'EB Garamond', serif;
  color: #333;
  font-size: 24px;
  font-weight: bold;
`;

const Label = styled.label`
  font-size: 16px;
  color: #333;
  font-family: 'Raleway', sans-serif;
  font-weight: 600;
  display: flex;
  align-items: center;
  margin-bottom: 5px;
`;

const ErrorText = styled.span`
  color: #e74c3c;
  font-size: 14px;
  margin-top: 5px;
  display: block;
`;

const InfoIcon = styled(FiInfo)`
  margin-left: 5px;
  color: #888;
  cursor: pointer;
`;

const Input = styled.input`
  padding: 12px 15px;
  border-radius: 5px;
  border: 1px solid rgba(0, 0, 0, 0.2);
  font-size: 16px;
  background-color: #fefefe;
  font-family: 'Raleway', sans-serif;
  transition: border-color 0.3s;
  box-sizing: border-box;
  &:focus {
    border-color: #5b9e5d;
    outline: none;
  }
`;

const FullWidthTextarea = styled.textarea`
  padding: 12px 15px;
  border-radius: 5px;
  border: 1px solid rgba(0, 0, 0, 0.2);
  font-size: 16px;
  width: 100%;
  background-color: #fefefe;
  font-family: 'Raleway', sans-serif;
  resize: vertical;
  transition: border-color 0.3s;
  box-sizing: border-box;
  &:focus {
    border-color: #5b9e5d;
    outline: none;
  }
`;

const IngredientGroup = styled.div`
  display: flex;
  flex-direction: column;
  gap: 10px;
  margin-bottom: 10px;
  position: relative;
`;

const Row = styled.div`
  display: flex;
  gap: 10px;
  align-items: center;
  justify-content: space-between;
  flex-wrap: nowrap;
`;

const SmallInput = styled(Input)`
  width: 30%; /* Adjusted width to fit three items in the same row */
`;

const Select = styled.select`
  padding: 12px 15px;
  border-radius: 5px;
  border: 1px solid rgba(0, 0, 0, 0.2);
  font-size: 16px;
  width: 30%; /* Adjusted width to fit three items in the same row */
  background-color: #fefefe;
  font-family: 'Raleway', sans-serif;
  transition: border-color 0.3s;
  box-sizing: border-box;
  &:focus {
    border-color: #5b9e5d;
    outline: none;
  }
`;

const Button = styled.button`
  border-radius: 8px;
  padding: 10px 16px;
  font-size: 16px;
  font-family: 'Raleway', sans-serif;
  cursor: pointer;
  color: #FFFFFF;
  border: none;
  transition: background-color 0.3s, transform 0.2s;
  box-shadow: 0px 5px 15px rgba(0, 0, 0, 0.2);
  width: 100%;
  box-sizing: border-box;
  &:hover {
    transform: translateY(-3px);
  }
`;

const DeleteButton = styled(Button)`
  background-color: #ff5c5c;
  &:hover {
    background-color: #e64545;
  }
`;

const AddButton = styled(Button)`
  background-color: #5b9e5d;
  &:hover {
    background-color: #4a8c50;
  }
`;

const SubstituteButton = styled(Button)`
  background-color: #f0a202;
  &:hover {
    background-color: #e68a00;
  }
`;

const SubmitButton = styled(Button)`
  background-color: #8B5E3C; /* Brown color */
  &:hover {
    background-color: #70462c; /* Darker shade on hover */
  }
`;

const AddRecipePage = () => {
  const [recipeName, setRecipeName] = useState("");
  const [description, setDescription] = useState("");
  const [ingredients, setIngredients] = useState([{ quantity: "", measure: "", name: "", substitute: "", showSubstitute: false }]);
  const [instructions, setInstructions] = useState([""]);
  const [errors, setErrors] = useState({});

  const validateForm = () => {
    let valid = true;
    const newErrors = {};

    if (description.length < 10) {
      newErrors.description = "Description must be at least 10 characters.";
      valid = false;
    }

    ingredients.forEach((ingredient, index) => {
      if (!/^\d+$/.test(ingredient.quantity)) {
        newErrors[`ingredientQuantity${index}`] = "Quantity must be a number.";
        valid = false;
      }
      if (!ingredient.name || /\d/.test(ingredient.name)) {
        newErrors[`ingredientName${index}`] = "Ingredient name must be letters only.";
        valid = false;
      }
    });

    instructions.forEach((step, index) => {
      if (!step) {
        newErrors[`instruction${index}`] = "This step is empty.";
        valid = false;
      }
    });

    setErrors(newErrors);
    return valid;
  };

  const handleAddIngredient = () => {
    setIngredients([...ingredients, { quantity: "", measure: "", name: "", substitute: "", showSubstitute: false }]);
  };

  const handleDeleteIngredient = (index) => {
    setIngredients(ingredients.filter((_, i) => i !== index));
  };

  const toggleSubstitute = (index) => {
    const newIngredients = [...ingredients];
    newIngredients[index].showSubstitute = !newIngredients[index].showSubstitute;
    setIngredients(newIngredients);
  };

  const handleAddStep = () => {
    setInstructions([...instructions, ""]);
  };

  const handleDeleteStep = (index) => {
    setInstructions(instructions.filter((_, i) => i !== index));
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      const recipeData = { recipeName, description, ingredients, instructions };
      console.log("Recipe Data:", recipeData);
      alert("Recipe submitted successfully!");
    }
  };

  return (
    <PageWrapper>
      <Form onSubmit={handleFormSubmit}>
        <Title>Add a New Recipe</Title>

        <FormGroup>
          <Label htmlFor="recipeName">What would you like to name your recipe? <InfoIcon /></Label>
          <Input
            type="text"
            id="recipeName"
            placeholder="E.g., Grandma's Apple Pie"
            value={recipeName}
            onChange={(e) => setRecipeName(e.target.value)}
          />
        </FormGroup>

        <FormGroup>
          <Label htmlFor="description">Describe your recipe:</Label>
          <FullWidthTextarea
            id="description"
            placeholder="Add a short description of the recipe"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
          {errors.description && <ErrorText>{errors.description}</ErrorText>}
        </FormGroup>

        <FormGroup>
          <Label>What ingredients will you need?</Label>
          {ingredients.map((ingredient, index) => (
            <IngredientGroup key={index}>
              <Row>
                <SmallInput
                  type="text"
                  placeholder="Quantity"
                  value={ingredient.quantity}
                  onChange={(e) => {
                    const newIngredients = [...ingredients];
                    newIngredients[index].quantity = e.target.value;
                    setIngredients(newIngredients);
                  }}
                />
                {errors[`ingredientQuantity${index}`] && <ErrorText>{errors[`ingredientQuantity${index}`]}</ErrorText>}
                <Select
                  value={ingredient.measure}
                  onChange={(e) => {
                    const newIngredients = [...ingredients];
                    newIngredients[index].measure = e.target.value;
                    setIngredients(newIngredients);
                  }}
                >
                  <option value="">Select Measure</option>
                  <option value="g">g</option>
                  <option value="kg">kg</option>
                  <option value="ml">ml</option>
                  <option value="cup">Cup</option>
                  <option value="tbsp">Tbsp</option>
                  <option value="tsp">Tsp</option>
                </Select>
                <SmallInput
                  type="text"
                  placeholder="Ingredient"
                  value={ingredient.name}
                  onChange={(e) => {
                    const newIngredients = [...ingredients];
                    newIngredients[index].name = e.target.value;
                    setIngredients(newIngredients);
                  }}
                />
                {errors[`ingredientName${index}`] && <ErrorText>{errors[`ingredientName${index}`]}</ErrorText>}
                <DeleteButton type="button" onClick={() => handleDeleteIngredient(index)}>
                  Delete
                </DeleteButton>
              </Row>
              <SubstituteButton type="button" onClick={() => toggleSubstitute(index)}>
                {ingredient.showSubstitute ? "- Substitute" : "+ Substitute"}
              </SubstituteButton>
              {ingredient.showSubstitute && (
                <Input
                  type="text"
                  placeholder="Substitute Ingredient"
                  value={ingredient.substitute}
                  onChange={(e) => {
                    const newIngredients = [...ingredients];
                    newIngredients[index].substitute = e.target.value;
                    setIngredients(newIngredients);
                  }}
                />
              )}
            </IngredientGroup>
          ))}
          <AddButton type="button" onClick={handleAddIngredient}>+ Add Another Ingredient</AddButton>
        </FormGroup>

        <FormGroup>
          <Label>Instructions:</Label>
          {instructions.map((step, index) => (
            <FormGroup key={index}>
              <FullWidthTextarea
                placeholder={`Step ${index + 1}`}
                value={step}
                onChange={(e) => {
                  const newInstructions = [...instructions];
                  newInstructions[index] = e.target.value;
                  setInstructions(newInstructions);
                }}
              />
              {errors[`instruction${index}`] && <ErrorText>{errors[`instruction${index}`]}</ErrorText>}
              <DeleteButton type="button" onClick={() => handleDeleteStep(index)} style={{ marginTop: '5px' }}>
                Delete Step
              </DeleteButton>
            </FormGroup>
          ))}
          <AddButton type="button" onClick={handleAddStep}>+ Add Step</AddButton>
        </FormGroup>

        <FormGroup>
          <Label htmlFor="photo">Photo:</Label>
          <Input type="file" id="photo" />
        </FormGroup>

        <SubmitButton type="submit">Submit Recipe</SubmitButton>
      </Form>
    </PageWrapper>
  );
};

export default AddRecipePage;
