import React from 'react';
import { Link } from 'react-router-dom';
import './css/Navbar.css';
import logo from './logo.png';  // Ensure your logo is imported

function Navbar({ isLoggedIn, setIsLoggedIn }) {
  return (
    <>
      {/* Top Navbar for registered users */}
      {isLoggedIn && (
        <nav className="top-navbar">
          {/* Left section: Logo */}
          <div className="navbar-logo">
            <Link to="/">
              <img src={logo} alt="Culinareat Logo" className="logo" />
            </Link>
          </div>

          {/* Right section: Profile, Chatbot, Preferences, and Logout */}
          <div className="navbar-actions">
            <button className="navbar-button profile-btn">
              <i className="fas fa-user"></i> My Profile
            </button>
            <button className="navbar-button chatbot-btn">
              <i className="fas fa-comments"></i> Open Chatbot
            </button>
            <button className="navbar-button preferences-btn">
              <i className="fas fa-cog"></i> My Preferences
            </button>
            <button className="logout-button" onClick={() => setIsLoggedIn(false)}>
              Logout <i className="fas fa-sign-out-alt"></i>
            </button>
          </div>
        </nav>
      )}

      {/* Bottom Navbar for logged-in users */}
      {isLoggedIn && (
        <nav className="bottom-navbar">
          <ul className="navbar-links">
            <li><Link to="/">Home</Link></li>
            <li><Link to="/articles">Articles</Link></li>
            <li><Link to="/podcasts">Podcasts</Link></li>
            <li><Link to="/expert-advice">Expert Advice</Link></li>
            <li><Link to="/faqs">FAQs</Link></li>
          </ul>

          {/* Right section: Search bar aligned to the far right */}
          <div className="search-bar">
            <input type="text" placeholder="Search..." />
            <button type="submit">
              <i className="fas fa-search"></i>
            </button>
          </div>
        </nav>
      )}

      {/* Navbar for not logged-in users remains unchanged */}
      {!isLoggedIn && (
        <nav className="navbar">
          <div className="navbar-logo">
            <Link to="/">
              <img src={logo} alt="Culinareat Logo" className="logo" />
            </Link>
          </div>
          <ul className="navbar-links navbar-links-not-logged">
            <li><Link to="/register">Register</Link></li>
            <li><Link to="/login">Login</Link></li>
          </ul>
        </nav>
      )}
    </>
  );
}

export default Navbar;
