import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import axios from 'axios';
import pic3 from './images/pic3.jpeg'; // Import the image

const PageWrapper = styled.div`
  display: flex;
  flex-direction: row;
  height: 100vh;
  width: 100vw;
  background: linear-gradient(135deg, #e9e4e0 0%, #f3ece8 100%);
`;

const RightFormContainer = styled.div`
  flex: 1;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const FormContainer = styled.form`
  width: 100%;
  max-width: 400px;
  text-align: center;
  padding: 30px;
  border-radius: 15px;
  background: linear-gradient(135deg, #ffffff 0%, #f5f5f5 100%);
  box-shadow: 0px 8px 24px rgba(0, 0, 0, 0.2);
`;

const ModernHeadline = styled.h1`
  font-size: 2.5rem;
  color: #333;
  font-family: 'Montserrat', sans-serif;
  margin-bottom: 10px;
  font-weight: 700;
`;

const SubHeadline = styled.h2`
  font-size: 1.2rem;
  color: #666;
  font-family: 'Raleway', sans-serif;
  margin-bottom: 20px;
`;

const LeftImageContainer = styled.div`
  flex: 1;
  position: relative;
  display: flex;
  justify-content: center;
  align-items: center;
  overflow: hidden;
  background-color: #f0f0f0;
`;

const OverlayText = styled.div`
  position: relative;
  color: #ffffff;
  font-size: 2rem;
  text-align: center;
  padding: 30px;
  background-color: rgba(0, 0, 0, 0.5);
  border-radius: 10px;
  font-family: 'Dancing Script', cursive;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.7);
  letter-spacing: 1px;
  line-height: 1.5;
  text-shadow: 2px 2px 8px rgba(0, 0, 0, 0.8);
  z-index: 1;
`;

function LoginPage({ setIsLoggedIn }) {
  const [formData, setFormData] = useState({
    UserName: '',
    Password: '',
  });
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const validateUsername = (value) => (!value ? 'Username is required.' : '');
  const validatePassword = (value) =>
    !value ? 'Password is required.' : value.length < 6 ? 'Password must be at least 6 characters.' : '';

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));

    if (name === 'UserName') setErrors((prev) => ({ ...prev, UserName: validateUsername(value) }));
    if (name === 'Password') setErrors((prev) => ({ ...prev, Password: validatePassword(value) }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const usernameError = validateUsername(formData.UserName);
    const passwordError = validatePassword(formData.Password);

    if (usernameError || passwordError) {
      setErrors({ UserName: usernameError, Password: passwordError });
      return;
    }

    try {
      await axios.post('http://localhost:5001/login', formData, { headers: { 'Content-Type': 'application/json' } });
      setIsLoggedIn(true);
      localStorage.setItem('isLoggedIn', 'true');
      navigate('/');
    } catch {
      setErrors((prev) => ({ ...prev, api: 'Invalid username or password.' }));
    }
  };

  return (
    <PageWrapper>
      <LeftImageContainer>
        <img
          src={pic3}
          alt="Culinary Background"
          onError={(e) => {
            console.error('Error loading image:', e.target.src);
            e.target.style.display = 'none'; // Hide the image if it fails to load
          }}
          style={{
            width: '100%',
            height: '100%',
            objectFit: 'cover',
            position: 'absolute',
            zIndex: -1,
          }}
        />
        <OverlayText>
          Glad to see you back! <br />
          Login to continue your culinary journey.
        </OverlayText>
      </LeftImageContainer>
      <RightFormContainer>
        <FormContainer onSubmit={handleSubmit}>
          <ModernHeadline>CulinarEAT</ModernHeadline>
          <SubHeadline>Welcome Back!</SubHeadline>
          <FormGroup>
            <Label htmlFor="UserName">Username</Label>
            <InputWrapper>
              <Input
                type="text"
                id="UserName"
                name="UserName"
                value={formData.UserName}
                onChange={handleChange}
                isError={!!errors.UserName}
              />
              <CheckmarkIcon isValid={!errors.UserName && formData.UserName}>✔</CheckmarkIcon>
            </InputWrapper>
            <ErrorText isVisible={!!errors.UserName}>{errors.UserName}</ErrorText>
          </FormGroup>
          <FormGroup>
            <Label htmlFor="Password">Password</Label>
            <InputWrapper>
              <Input
                type="password"
                id="Password"
                name="Password"
                value={formData.Password}
                onChange={handleChange}
                isError={!!errors.Password}
              />
              <CheckmarkIcon isValid={!errors.Password && formData.Password}>✔</CheckmarkIcon>
            </InputWrapper>
            <ErrorText isVisible={!!errors.Password}>{errors.Password}</ErrorText>
          </FormGroup>
          <SubmitButton type="submit">Sign In</SubmitButton>
          {errors.api && <ErrorText isVisible>{errors.api}</ErrorText>}
        </FormContainer>
      </RightFormContainer>
    </PageWrapper>
  );
}

export default LoginPage;
