import React, { useState } from 'react';
import styled from 'styled-components';

const PageWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 40px;
  min-height: 100vh;
  background-color: #D6C0B3;
  font-family: 'Raleway', sans-serif;
`;

// Other styled components remain the same...

const AddRecipePage = () => {
  const [recipeName, setRecipeName] = useState("");
  const [description, setDescription] = useState("");
  const [ingredients, setIngredients] = useState([{ quantity: "", measure: "", name: "", substitute: "" }]);
  const [instructions, setInstructions] = useState([""]);

  const handleRecipeNameChange = (e) => setRecipeName(e.target.value);
  const handleDescriptionChange = (e) => setDescription(e.target.value);

  const handleIngredientChange = (index, field, value) => {
    const newIngredients = [...ingredients];
    newIngredients[index][field] = value;
    setIngredients(newIngredients);
  };

  const handleAddIngredient = () => {
    setIngredients([...ingredients, { quantity: "", measure: "", name: "", substitute: "" }]);
  };

  const handleDeleteIngredient = (index) => {
    setIngredients(ingredients.filter((_, i) => i !== index));
  };

  const handleAddStep = () => {
    setInstructions([...instructions, ""]);
  };

  const handleDeleteStep = (index) => {
    setInstructions(instructions.filter((_, i) => i !== index));
  };

  const handleStepChange = (index, value) => {
    const newInstructions = [...instructions];
    newInstructions[index] = value;
    setInstructions(newInstructions);
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    const recipeData = {
      recipeName,
      description,
      ingredients,
      instructions,
    };
    console.log("Recipe Data:", recipeData);
    // Additional form handling logic
  };

  return (
    <PageWrapper>
      <Title>Add a New Recipe</Title>
      <Form onSubmit={handleFormSubmit}>
        <FormGroup>
          <Label htmlFor="recipeName">Recipe Name:</Label>
          <Input
            type="text"
            id="recipeName"
            value={recipeName}
            onChange={handleRecipeNameChange}
          />
        </FormGroup>

        <FormGroup>
          <Label htmlFor="description">Description:</Label>
          <FullWidthTextarea
            id="description"
            placeholder="Add a short description of the recipe"
            value={description}
            onChange={handleDescriptionChange}
          />
        </FormGroup>

        <Subtitle>Ingredients</Subtitle>
        {ingredients.map((ingredient, index) => (
          <IngredientGroup key={index}>
            <Input
              type="text"
              placeholder="Quantity"
              value={ingredient.quantity}
              onChange={(e) => handleIngredientChange(index, 'quantity', e.target.value)}
            />
            <Select
              value={ingredient.measure}
              onChange={(e) => handleIngredientChange(index, 'measure', e.target.value)}
            >
              {/* Option elements remain the same */}
            </Select>
            <Input
              type="text"
              placeholder="Ingredient"
              value={ingredient.name}
              onChange={(e) => handleIngredientChange(index, 'name', e.target.value)}
            />
            <Button
              className="delete"
              onClick={() => handleDeleteIngredient(index)}
            >
              - Delete
            </Button>
          </IngredientGroup>
        ))}
        <Button className="add" onClick={handleAddIngredient}>+ Add Ingredient</Button>

        <Subtitle>Instructions</Subtitle>
        {instructions.map((step, index) => (
          <FormGroup key={index}>
            <FullWidthTextarea
              placeholder={`Step ${index + 1}`}
              value={step}
              onChange={(e) => handleStepChange(index, e.target.value)}
            />
            <Button className="delete" onClick={() => handleDeleteStep(index)}>
              - Delete
            </Button>
          </FormGroup>
        ))}
        <Button className="add" onClick={handleAddStep}>+ Add Step</Button>

        <FormGroup>
          <Label htmlFor="photo">Photo:</Label>
          <Input type="file" id="photo" />
        </FormGroup>

        <SubmitButton type="submit">Submit Recipe</SubmitButton>
      </Form>
    </PageWrapper>
  );
};

export default AddRecipePage;
