import React, { useState, useEffect } from 'react';
import './css/AddRecipePage.css';

function AddRecipePage() {
    const [recipeName, setRecipeName] = useState('');
    const [description, setDescription] = useState('');
    const [ingredients, setIngredients] = useState([{ quantity: '', measure: '', ingredient: '' }]);
    const [instructions, setInstructions] = useState(['']);
    const [photo, setPhoto] = useState(null);
    const [successMessage, setSuccessMessage] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const [formErrors, setFormErrors] = useState({});

    // Fixed list of common cooking and baking measurements
    const measures = [
        { MeasureID: 'mg', MeasureName: 'mg' },
        { MeasureID: 'g', MeasureName: 'g' },
        { MeasureID: 'kg', MeasureName: 'kg' },
        { MeasureID: 'oz', MeasureName: 'oz' },
        { MeasureID: 'lb', MeasureName: 'lb' },
        { MeasureID: 'ml', MeasureName: 'ml' },
        { MeasureID: 'liter', MeasureName: 'liter' },
        { MeasureID: 'cup', MeasureName: 'Cup' },
        { MeasureID: 'tbsp', MeasureName: 'Tbsp' },
        { MeasureID: 'tsp', MeasureName: 'Tsp' },
        { MeasureID: 'fl oz', MeasureName: 'fl oz' },
        { MeasureID: 'quart', MeasureName: 'Quart' },
        { MeasureID: 'pint', MeasureName: 'Pint' },
        { MeasureID: 'unit', MeasureName: 'Unit' }
    ];

    // Validation helper functions
    const isOnlyLetters = (text) => /^[A-Za-z\s]+$/.test(text);
    const isNumeric = (value) => /^[0-9]+$/.test(value);
    const isImageFile = (file) => file && file.type.startsWith("image/");

    // Validate entire form before submission
    const validateForm = () => {
        const errors = {};
        if (!recipeName || !isOnlyLetters(recipeName)) {
            errors.recipeName = 'Recipe name should contain only letters and spaces.';
        }
        if (!description || description.length < 10) {
            errors.description = 'Description should be at least 10 characters long.';
        }
        ingredients.forEach((ingredient, index) => {
            if (!isNumeric(ingredient.quantity)) {
                errors[`quantity${index}`] = 'Quantity should contain only numbers.';
            }
            if (!ingredient.measure) {
                errors[`measure${index}`] = 'Please select a measure.';
            }
            if (!isOnlyLetters(ingredient.ingredient)) {
                errors[`ingredient${index}`] = 'Ingredient name should contain only letters and spaces.';
            }
        });
        instructions.forEach((instruction, index) => {
            if (!instruction || instruction.length < 5) {
                errors[`instruction${index}`] = 'Each step should be at least 5 characters long.';
            }
        });
        if (photo && !isImageFile(photo)) {
            errors.photo = 'Please upload a valid image file.';
        }
        setFormErrors(errors);
        return Object.keys(errors).length === 0;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!validateForm()) {
            setErrorMessage('Please fix the errors in the form.');
            return;
        }

        const formData = new FormData();
        formData.append('recipeName', recipeName);
        formData.append('description', description);
        formData.append('ingredients', JSON.stringify(ingredients));
        formData.append('instructions', JSON.stringify(instructions));
        if (photo) formData.append('photo', photo);

        try {
            // Replace with your actual API endpoint
            const response = await fetch('http://localhost:5001/add-recipe', {
                method: 'POST',
                body: formData,
                headers: { 'Content-Type': 'multipart/form-data' }
            });
            const result = await response.json();
            setSuccessMessage('Recipe added successfully!');
            setErrorMessage('');
            console.log(result);
        } catch (error) {
            setErrorMessage('An error occurred while adding the recipe. Please try again.');
            console.error('Error adding recipe:', error);
        }
    };

    return (
        <div className="add-recipe-page">
            <h2>Add a New Recipe</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="recipeName">Recipe Name:</label>
                    <input 
                        type="text" 
                        id="recipeName" 
                        value={recipeName} 
                        onChange={(e) => setRecipeName(e.target.value)} 
                        required 
                    />
                    {formErrors.recipeName && <span className="error-text">{formErrors.recipeName}</span>}
                </div>

                <div className="form-group">
                    <label htmlFor="description">Description:</label>
                    <textarea
                        id="description"
                        value={description}
                        onChange={(e) => setDescription(e.target.value)}
                        placeholder="Add a short description of the recipe"
                        required
                        className="full-width-textarea"
                    />
                    {formErrors.description && <span className="error-text">{formErrors.description}</span>}
                </div>

                <h3>Ingredients</h3>
                {ingredients.map((ingredient, index) => (
                    <div key={index} className="ingredient-group">
                        <input 
                            type="text" 
                            name="quantity" 
                            placeholder="Quantity" 
                            value={ingredient.quantity} 
                            onChange={(e) => handleIngredientChange(index, e)} 
                            required 
                        />
                        {formErrors[`quantity${index}`] && <span className="error-text">{formErrors[`quantity${index}`]}</span>}
                        <select
                            name="measure"
                            value={ingredient.measure}
                            onChange={(e) => handleIngredientChange(index, e)}
                            required
                        >
                            <option value="">Select Measure</option>
                            {measures.map(measure => (
                                <option key={measure.MeasureID} value={measure.MeasureID}>
                                    {measure.MeasureName}
                                </option>
                            ))}
                        </select>
                        {formErrors[`measure${index}`] && <span className="error-text">{formErrors[`measure${index}`]}</span>}
                        <input 
                            type="text" 
                            name="ingredient" 
                            placeholder="Ingredient" 
                            value={ingredient.ingredient} 
                            onChange={(e) => handleIngredientChange(index, e)} 
                            required 
                        />
                        {formErrors[`ingredient${index}`] && <span className="error-text">{formErrors[`ingredient${index}`]}</span>}
                    </div>
                ))}
                <div className="button-group-inline">
                    <button type="button" onClick={handleAddIngredient} className="add-button">+ Add Ingredient</button>
                    <button type="button" onClick={() => handleDeleteIngredient(ingredients.length - 1)} className="delete-button">- Delete</button>
                </div>

                <h3>Instructions</h3>
                {instructions.map((instruction, index) => (
                    <div key={index} className="instruction-group">
                        <textarea
                            placeholder={`Step ${index + 1}`}
                            value={instruction}
                            onChange={(e) => handleInstructionChange(index, e)}
                            required
                        />
                        {formErrors[`instruction${index}`] && <span className="error-text">{formErrors[`instruction${index}`]}</span>}
                    </div>
                ))}
                <div className="button-group-inline">
                    <button type="button" onClick={handleAddInstruction} className="add-button">+ Add Step</button>
                    <button type="button" onClick={() => handleDeleteInstruction(instructions.length - 1)} className="delete-button">- Delete</button>
                </div>

                <div className="form-group form-group-photo">
                    <label htmlFor="photo">Photo:</label>
                    <input type="file" id="photo" onChange={handlePhotoChange} />
                    {formErrors.photo && <span className="error-text">{formErrors.photo}</span>}
                </div>

                <button type="submit" className="submit-button">Submit Recipe</button>

                {successMessage && <div className="success">{successMessage}</div>}
                {errorMessage && <div className="error">{errorMessage}</div>}
            </form>
        </div>
    );
}

export default AddRecipePage;
