import React, { useState } from 'react';
import styled from 'styled-components';
import { FiInfo } from 'react-icons/fi';

const PageWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 40px;
  min-height: 100vh;
  background-color: #D6C0B3;
  font-family: 'Raleway', sans-serif;
`;

const Form = styled.form`
  width: 100%;
  max-width: 780px;
  padding: 30px;
  border-radius: 10px;
  background: transparent;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1);
  border: 1px solid rgba(200, 200, 200, 0.5);
`;

const FormGroup = styled.div`
  margin-bottom: 20px;
  display: flex;
  flex-direction: column;
  position: relative;
  min-height: 80px;
`;

const Title = styled.h2`
  text-align: center;
  margin-bottom: 30px;
  font-family: 'EB Garamond', serif;
  color: #333;
  font-size: 24px;
  font-weight: bold;
`;

const Label = styled.label`
  font-size: 16px;
  color: #333;
  font-family: 'Raleway', sans-serif;
  font-weight: 600;
  display: flex;
  align-items: center;
  margin-bottom: 5px;
`;

const ErrorText = styled.span`
  color: #e74c3c;
  font-size: 14px;
  margin-top: 4px;
  height: 18px;
  visibility: ${({ isVisible }) => (isVisible ? 'visible' : 'hidden')};
`;

const Input = styled.input`
  padding: 12px 15px;
  border-radius: 5px;
  border: 1px solid rgba(0, 0, 0, 0.2);
  font-size: 16px;
  background-color: #fefefe;
  font-family: 'Raleway', sans-serif;
  transition: border-color 0.3s;
  box-sizing: border-box;
  &:focus {
    border-color: #5b9e5d;
    outline: none;
  }
`;

const FullWidthTextarea = styled.textarea`
  padding: 12px 15px;
  border-radius: 5px;
  border: 1px solid rgba(0, 0, 0, 0.2);
  font-size: 16px;
  width: 100%;
  background-color: #fefefe;
  font-family: 'Raleway', sans-serif;
  resize: vertical;
  transition: border-color 0.3s;
  box-sizing: border-box;
  &:focus {
    border-color: #5b9e5d;
    outline: none;
  }
`;

const IngredientGroup = styled.div`
  display: flex;
  flex-direction: column;
  gap: 10px;
  margin-bottom: 10px;
  position: relative;
`;

const Row = styled.div`
  display: flex;
  gap: 10px;
  align-items: flex-start;
  justify-content: space-between;
  flex-wrap: nowrap;
`;

const SmallInputContainer = styled.div`
  display: flex;
  flex-direction: column;
  width: 35%;
`;

const SmallInput = styled(Input)`
  width: 100%;
`;

const Select = styled.select`
  padding: 12px 15px;
  border-radius: 5px;
  border: 1px solid rgba(0, 0, 0, 0.2);
  font-size: 16px;
  width: 35%;
  background-color: #fefefe;
  font-family: 'Raleway', sans-serif;
  transition: border-color 0.3s;
  box-sizing: border-box;
  &:focus {
    border-color: #5b9e5d;
    outline: none;
  }
`;

const Button = styled.button`
  border-radius: 8px;
  padding: 10px 16px;
  font-size: 16px;
  font-family: 'Raleway', sans-serif;
  cursor: pointer;
  color: #FFFFFF;
  border: none;
  transition: background-color 0.3s, transform 0.2s;
  box-shadow: 0px 5px 15px rgba(0, 0, 0, 0.2);
  width: 100%;
  box-sizing: border-box;
  &:hover {
    transform: translateY(-3px);
  }
`;

const DeleteButton = styled(Button)`
  background-color: #ff5c5c;
  width: 80px;
  height: 48px;
  text-align: center;
  display: flex;
  align-items: center;
  justify-content: center;
  &:hover {
    background-color: #e64545;
  }
  &::before {
    content: '- ';
  }
`;

const AddButton = styled(Button)`
  background-color: #5b9e5d;
  &:hover {
    background-color: #4a8c50;
  }
`;

const SubstituteButton = styled(Button)`
  background-color: #f0a202;
  &:hover {
    background-color: #e68a00;
  }
  width: auto;
  padding: 10px 20px;
`;

const InstructionRow = styled.div`
  display: flex;
  align-items: flex-start;
  gap: 10px;
`;

const SubmitButton = styled(Button)`
  background-color: #8B5E3C;
  &:hover {
    background-color: #70462c;
  }
`;

const AddRecipePage = () => {
  const [recipeName, setRecipeName] = useState("");
  const [description, setDescription] = useState("");
  const [ingredients, setIngredients] = useState([{ quantity: "", measure: "", name: "", substitute: "", showSubstitute: false }]);
  const [instructions, setInstructions] = useState([""]);
  const [errors, setErrors] = useState({});

  const handleRecipeNameChange = (value) => {
    setRecipeName(value);
    setErrors((prevErrors) => ({
      ...prevErrors,
      recipeName: value.length >= 3 || value === "" ? "" : "Recipe name must be at least 3 characters."
    }));
  };

  const handleDescriptionChange = (value) => {
    setDescription(value);
    setErrors((prevErrors) => ({
      ...prevErrors,
      description: value.length >= 10 || value === "" ? "" : "Description must be at least 10 characters."
    }));
  };

  const handleQuantityChange = (index, value) => {
    const newIngredients = [...ingredients];
    newIngredients[index].quantity = value;
    setIngredients(newIngredients);
    setErrors((prevErrors) => ({
      ...prevErrors,
      [`ingredientQuantity${index}`]: /^\d+$/.test(value) || value === "" ? "" : "Quantity must be a number."
    }));
  };

  const handleIngredientNameChange = (index, value) => {
    const newIngredients = [...ingredients];
    newIngredients[index].name = value;
    setIngredients(newIngredients);
    setErrors((prevErrors) => ({
      ...prevErrors,
      [`ingredientName${index}`]: /^[a-zA-Z\s]+$/.test(value) || value === "" ? "" : "Ingredient name must contain only letters."
    }));
  };

  const handleStepChange = (index, value) => {
    const newInstructions = [...instructions];
    newInstructions[index] = value;
    setInstructions(newInstructions);
    setErrors((prevErrors) => ({
      ...prevErrors,
      [`instruction${index}`]: value.length >= 10 || value === "" ? "" : "Each step must be at least 10 characters."
    }));
  };

  const handleAddIngredient = () => {
    setIngredients([...ingredients, { quantity: "", measure: "", name: "", substitute: "", showSubstitute: false }]);
  };

  const handleDeleteIngredient = (index) => {
    setIngredients(ingredients.filter((_, i) => i !== index));
  };

  const toggleSubstitute = (index) => {
    const newIngredients = [...ingredients];
    newIngredients[index].showSubstitute = !newIngredients[index].showSubstitute;
    setIngredients(newIngredients);
  };

  const handleAddStep = () => {
    if (instructions.length === 0 || instructions[instructions.length - 1].length >= 10) {
      setInstructions([...instructions, ""]);
    } else {
      setErrors((prevErrors) => ({
        ...prevErrors,
        [`instruction${instructions.length - 1}`]: "Please complete this step before adding a new one."
      }));
    }
  };

  const handleDeleteStep = (index) => {
    const newInstructions = instructions.filter((_, i) => i !== index);
    setInstructions(newInstructions);
    setErrors((prevErrors) => {
      const newErrors = { ...prevErrors };
      delete newErrors[`instruction${index}`];
      return newErrors;
    });
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    alert("Recipe submitted successfully!");
  };

  return (
    <PageWrapper>
      <Form onSubmit={handleFormSubmit}>
        <Title>Add a New Recipe</Title>

        <FormGroup>
          <Label htmlFor="recipeName">What would you like to name your recipe? <FiInfo /></Label>
          <Input
            type="text"
            id="recipeName"
            placeholder="E.g., Grandma's Apple Pie"
            value={recipeName}
            onChange={(e) => handleRecipeNameChange(e.target.value)}
            aria-label="Recipe name"
          />
          <ErrorText isVisible={!!errors.recipeName}>{errors.recipeName}</ErrorText>
        </FormGroup>

        <FormGroup>
          <Label htmlFor="description">Describe your recipe:</Label>
          <FullWidthTextarea
            id="description"
            placeholder="Add a short description of the recipe"
            value={description}
            onChange={(e) => handleDescriptionChange(e.target.value)}
            aria-label="Recipe description"
          />
          <ErrorText isVisible={!!errors.description}>{errors.description}</ErrorText>
        </FormGroup>

        <FormGroup>
          <Label>What ingredients will you need?</Label>
          {ingredients.map((ingredient, index) => (
            <IngredientGroup key={index}>
              <Row>
                <SmallInputContainer>
                  <SmallInput
                    type="text"
                    placeholder="Quantity"
                    value={ingredient.quantity}
                    onChange={(e) => handleQuantityChange(index, e.target.value)}
                    aria-label={`Quantity for ingredient ${index + 1}`}
                  />
                  <ErrorText isVisible={!!errors[`ingredientQuantity${index}`]}>{errors[`ingredientQuantity${index}`]}</ErrorText>
                </SmallInputContainer>
                
                <Select
                  value={ingredient.measure}
                  onChange={(e) => {
                    const newIngredients = [...ingredients];
                    newIngredients[index].measure = e.target.value;
                    setIngredients(newIngredients);
                  }}
                >
                  <option value="">Select Measure</option>
                  <option value="g">g</option>
                  <option value="kg">kg</option>
                  <option value="ml">ml</option>
                  <option value="cup">Cup</option>
                  <option value="tbsp">Tbsp</option>
                  <option value="tsp">Tsp</option>
                </Select>

                <SmallInputContainer>
                  <SmallInput
                    type="text"
                    placeholder="Ingredient"
                    value={ingredient.name}
                    onChange={(e) => handleIngredientNameChange(index, e.target.value)}
                    aria-label={`Ingredient name for ingredient ${index + 1}`}
                  />
                  <ErrorText isVisible={!!errors[`ingredientName${index}`]}>{errors[`ingredientName${index}`]}</ErrorText>
                </SmallInputContainer>
                
                <DeleteButton type="button" onClick={() => handleDeleteIngredient(index)} aria-label={`Delete ingredient ${index + 1}`}>
                  Delete
                </DeleteButton>
              </Row>
              <SubstituteButton type="button" onClick={() => toggleSubstitute(index)}>
                {ingredient.showSubstitute ? "- Substitute" : "+ Substitute"}
              </SubstituteButton>
              {ingredient.showSubstitute && (
                <Input
                  type="text"
                  placeholder="Substitute Ingredient"
                  value={ingredient.substitute}
                  onChange={(e) => {
                    const newIngredients = [...ingredients];
                    newIngredients[index].substitute = e.target.value;
                    setIngredients(newIngredients);
                  }}
                />
              )}
            </IngredientGroup>
          ))}
          <AddButton type="button" onClick={handleAddIngredient} aria-label="Add another ingredient">+ Add Another Ingredient</AddButton>
        </FormGroup>

        <FormGroup>
          <Label>Instructions:</Label>
          {instructions.map((step, index) => (
            <InstructionRow key={index}>
              <FullWidthTextarea
                placeholder={`Step ${index + 1}`}
                value={step}
                onChange={(e) => handleStepChange(index, e.target.value)}
                aria-label={`Instruction step ${index + 1}`}
              />
              <DeleteButton type="button" onClick={() => handleDeleteStep(index)} aria-label={`Delete step ${index + 1}`}>
                Delete Step
              </DeleteButton>
              <ErrorText isVisible={!!errors[`instruction${index}`]}>{errors[`instruction${index}`]}</ErrorText>
            </InstructionRow>
          ))}
          <AddButton type="button" onClick={handleAddStep} style={{ marginTop: '10px', alignSelf: 'flex-start' }} aria-label="Add step">+ Add Step</AddButton>
        </FormGroup>

        <FormGroup>
          <Label htmlFor="photo">Photo:</Label>
          <Input type="file" id="photo" />
        </FormGroup>

        <SubmitButton type="submit">Submit Recipe</SubmitButton>
      </Form>
    </PageWrapper>
  );
};

export default AddRecipePage;
