import React, { useState } from 'react';
import styled from 'styled-components';
import { FiInfo } from 'react-icons/fi';

const PageWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 40px;
  min-height: 100vh;
  background-color: #D6C0B3;
  font-family: 'Raleway', sans-serif;
`;

const Form = styled.form`
  width: 100%;
  max-width: 780px;
  padding: 30px;
  border-radius: 10px;
  background: linear-gradient(135deg, #EFE2D0, #D6C0B3);
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.1);
`;

const FormGroup = styled.div`
  margin-bottom: 20px;
  display: flex;
  flex-direction: column;
`;

const Title = styled.h2`
  text-align: center;
  margin-bottom: 30px;
  font-family: 'EB Garamond', serif;
  color: #333;
  font-size: 24px;
  font-weight: bold;
`;

const Label = styled.label`
  font-size: 16px;
  color: #333;
  font-family: 'Poppins', sans-serif;
  font-weight: 600;
  display: flex;
  align-items: center;
  margin-bottom: 5px;
`;

const InfoIcon = styled(FiInfo)`
  margin-left: 5px;
  color: #888;
  cursor: pointer;
`;

const Input = styled.input`
  padding: 12px 15px;
  border-radius: 5px;
  border: 1px solid ${props => (props.error ? '#dc3545' : '#ddd')};
  font-size: 16px;
  width: 100%;
  background-color: #f9f9f9;
  transition: border-color 0.3s;
  &:focus {
    border-color: ${props => (props.error ? '#dc3545' : '#5b9e5d')};
    outline: none;
  }
`;

const Select = styled.select`
  padding: 12px 15px;
  border-radius: 5px;
  border: 1px solid #ddd;
  font-size: 16px;
  width: 100%;
  background-color: #f9f9f9;
  transition: border-color 0.3s;
  &:focus {
    border-color: #5b9e5d;
    outline: none;
  }
`;

const Button = styled.button`
  border-radius: 5px;
  padding: 12px 20px;
  font-size: 16px;
  font-family: 'Poppins', sans-serif;
  cursor: pointer;
  color: #FFFFFF;
  background-color: #5b9e5d;
  border: none;
  transition: background-color 0.3s, transform 0.2s;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1);
  margin-top: 10px;
  width: 100%;

  &:hover {
    background-color: #4a8c50;
    transform: translateY(-2px);
  }
`;

const ErrorText = styled.span`
  color: #dc3545;
  font-size: 14px;
  margin-top: 5px;
`;

const IngredientGroup = ({ ingredient, index, handleInputChange, handleDeleteIngredient, errors }) => (
  <div style={{ display: 'flex', gap: '10px', alignItems: 'center', marginBottom: '10px' }}>
    <Input
      type="text"
      placeholder="Quantity"
      value={ingredient.quantity}
      onChange={(e) => handleInputChange(e.target.value, index, 'quantity')}
      error={errors[`ingredientQuantity${index}`]}
    />
    <Select
      value={ingredient.measure}
      onChange={(e) => handleInputChange(e.target.value, index, 'measure')}
    >
      <option value="">Select Measure</option>
      <option value="g">g</option>
      <option value="kg">kg</option>
      <option value="ml">ml</option>
      <option value="cup">Cup</option>
      <option value="tbsp">Tbsp</option>
      <option value="tsp">Tsp</option>
    </Select>
    <Input
      type="text"
      placeholder="Ingredient"
      value={ingredient.name}
      onChange={(e) => handleInputChange(e.target.value, index, 'name')}
      error={errors[`ingredientName${index}`]}
    />
    <Button type="button" onClick={() => handleDeleteIngredient(index)} style={{ backgroundColor: '#dc3545', width: 'auto', marginTop: '0' }}>
      - Delete
    </Button>
  </div>
);

const AddRecipePage = () => {
  const [recipeName, setRecipeName] = useState("");
  const [description, setDescription] = useState("");
  const [ingredients, setIngredients] = useState([{ quantity: "", measure: "", name: "" }]);
  const [errors, setErrors] = useState({});

  const handleInputChange = (value, index, field) => {
    if (index !== null) {
      const updatedIngredients = [...ingredients];
      updatedIngredients[index][field] = value;
      setIngredients(updatedIngredients);
      if (value.trim()) setErrors((prev) => ({ ...prev, [`ingredient${field.charAt(0).toUpperCase() + field.slice(1)}${index}`]: '' }));
    }
  };

  const handleDeleteIngredient = (index) => {
    setIngredients(ingredients.filter((_, i) => i !== index));
  };

  const handleAddIngredient = () => setIngredients([...ingredients, { quantity: "", measure: "", name: "" }]);

  const handleFormSubmit = (e) => {
    e.preventDefault();
    const formErrors = {};
    if (!recipeName.trim()) formErrors.recipeName = "Recipe name is required";
    if (!description.trim()) formErrors.description = "Description is required";
    ingredients.forEach((ingredient, index) => {
      if (!ingredient.quantity.trim()) formErrors[`ingredientQuantity${index}`] = "Quantity is required";
      if (!ingredient.measure.trim()) formErrors[`ingredientMeasure${index}`] = "Measure is required";
      if (!ingredient.name.trim()) formErrors[`ingredientName${index}`] = "Ingredient name is required";
    });
    setErrors(formErrors);

    if (Object.keys(formErrors).length === 0) {
      const recipeData = { recipeName, description, ingredients };
      console.log("Recipe Data:", recipeData);
    }
  };

  return (
    <PageWrapper>
      <Form onSubmit={handleFormSubmit}>
        <Title>Add a New Recipe</Title>

        <FormGroup>
          <Label htmlFor="recipeName">What would you like to name your recipe? <InfoIcon /></Label>
          <Input
            type="text"
            id="recipeName"
            placeholder="E.g., Grandma's Apple Pie"
            value={recipeName}
            onChange={(e) => setRecipeName(e.target.value)}
            error={errors.recipeName}
          />
          {errors.recipeName && <ErrorText>{errors.recipeName}</ErrorText>}
        </FormGroup>

        <FormGroup>
          <Label htmlFor="description">Describe your recipe:</Label>
          <Input
            as="textarea"
            id="description"
            placeholder="Add a short description of the recipe"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            error={errors.description}
          />
          {errors.description && <ErrorText>{errors.description}</ErrorText>}
        </FormGroup>

        <FormGroup>
          <Label>What ingredients will you need?</Label>
          {ingredients.map((ingredient, index) => (
            <IngredientGroup
              key={index}
              ingredient={ingredient}
              index={index}
              handleInputChange={handleInputChange}
              handleDeleteIngredient={handleDeleteIngredient}
              errors={errors}
            />
          ))}
          <Button type="button" onClick={handleAddIngredient}>+ Add Another Ingredient</Button>
        </FormGroup>

        <SubmitButton type="submit">Submit Recipe</SubmitButton>
      </Form>
    </PageWrapper>
  );
};

export default AddRecipePage;
