import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function LoginPage({ setIsLoggedIn }) {
  const [formData, setFormData] = useState({
    UserName: '',
    Password: '',
  });

  const [errors, setErrors] = useState({});
  const [apiErrorMessage, setApiErrorMessage] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const validate = () => {
    let inputErrors = {};
    if (!formData.UserName) {
      inputErrors.UserName = 'Username is required';
    }
    if (!formData.Password) {
      inputErrors.Password = 'Password is required';
    }
    return inputErrors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const inputErrors = validate();

    if (Object.keys(inputErrors).length === 0) {
      try {
        const response = await axios.post('http://localhost:5001/login', formData, {
          headers: {
            'Content-Type': 'application/json',
          },
          withCredentials: true,
        });

        setIsLoggedIn(true);
        localStorage.setItem('isLoggedIn', 'true');
        localStorage.setItem('userName', formData.UserName);
        setApiErrorMessage('');
        navigate('/');
      } catch (error) {
        if (error.response && error.response.status === 401) {
          setApiErrorMessage('Invalid username or password.');
        } else {
          setApiErrorMessage('An error occurred during login. Please try again.');
        }
      }
    } else {
      setErrors(inputErrors);
    }
  };

  const styles = {
    loginPage: {
      display: 'flex',
      height: '100vh',
      width: '100vw',
      flexDirection: 'row-reverse',
      overflowX: 'hidden', // Prevent horizontal scrolling
    },
    rightImageContainer: {
      flex: 1,
      position: 'relative',
      backgroundImage: `url('../images/img4.png')`,
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
    },
    overlayText: {
      position: 'absolute',
      color: 'white',
      fontSize: '1.8rem',
      textAlign: 'center',
      padding: '20px',
      backgroundColor: 'rgba(0, 0, 0, 0.6)',
      borderRadius: '10px',
      fontFamily: "'Dancing Script', cursive",
      boxShadow: '0px 4px 10px rgba(0, 0, 0, 0.7)',
      letterSpacing: '1px',
      lineHeight: '1.5',
      maxWidth: '90%',
    },
    leftFormContainer: {
      flex: 1,
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      backgroundColor: '#fff',
      padding: '50px',
      boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    },
    formContainer: {
      width: '100%',
      maxWidth: '400px',
      textAlign: 'center',
    },
    headline: {
      fontSize: '2rem',
      color: '#333',
      marginBottom: '20px',
      fontFamily: "'Poppins', sans-serif",
      fontWeight: 'bold',
      textAlign: 'center',
    },
    formGroup: {
      marginBottom: '20px',
    },
    label: {
      display: 'block',
      marginBottom: '5px',
      fontSize: '14px',
      color: '#777',
    },
    input: {
      width: '100%',
      padding: '12px',
      borderRadius: '5px',
      border: '1px solid #ccc',
      fontSize: '16px',
    },
    submitButton: {
      width: '100%',
      padding: '12px',
      backgroundColor: '#4397c1',
      color: 'white',
      border: 'none',
      borderRadius: '4px',
      cursor: 'pointer',
      fontSize: '16px',
      marginTop: '20px',
    },
    errorBanner: {
      color: '#fff',
      backgroundColor: 'rgba(217, 83, 79, 0.7)',
      padding: '12px',
      borderRadius: '12px',
      marginTop: '20px',
      fontSize: '16px',
      textAlign: 'center',
      boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.2)',
    },
  };

  return (
    <div style={styles.loginPage}>
      <div style={styles.leftFormContainer}>
        <div style={styles.formContainer}>
          <h2 style={styles.headline}>Login to CulinarEAT</h2>
          <form onSubmit={handleSubmit}>
            <div style={styles.formGroup}>
              <label htmlFor="UserName" style={styles.label}>
                Username:
              </label>
              <input
                type="text"
                id="UserName"
                name="UserName"
                value={formData.UserName}
                onChange={handleChange}
                style={styles.input}
              />
              {errors.UserName && <div style={styles.errorBanner}>{errors.UserName}</div>}
            </div>

            <div style={styles.formGroup}>
              <label htmlFor="Password" style={styles.label}>
                Password:
              </label>
              <input
                type="password"
                id="Password"
                name="Password"
                value={formData.Password}
                onChange={handleChange}
                style={styles.input}
              />
              {errors.Password && <div style={styles.errorBanner}>{errors.Password}</div>}
            </div>

            <button type="submit" style={styles.submitButton}>
              Login
            </button>
          </form>

          {apiErrorMessage && <div style={styles.errorBanner}>{apiErrorMessage}</div>}
        </div>
      </div>

      <div style={styles.rightImageContainer}>
        <div style={styles.overlayText}>
          Glad to see you back! <br />
          Login to continue your culinary journey.
        </div>
      </div>
    </div>
  );
}

export default LoginPage;
