import React, { useState } from 'react';
import styled from 'styled-components';

// Styled Components
const PageWrapper = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    padding: 40px;
    min-height: 100vh;
    background-color: #D6C0B3;
    font-family: 'Raleway', sans-serif;
`;

const Form = styled.form`
    width: 100%;
    max-width: 800px;
    box-sizing: border-box;
`;

const FormGroup = styled.div`
    margin-bottom: 20px;
    display: flex;
    flex-direction: column;
    position: relative;
`;

const Label = styled.label`
    margin-bottom: 8px;
    font-size: 16px;
    color: #6f6666;
    font-family: 'Poppins', sans-serif;
    font-weight: bold;
`;

const FullWidthTextarea = styled.textarea`
    width: 100%;
    padding: 12px;
    border-radius: 5px;
    border: 1px solid #ccc;
    font-size: 16px;
    box-sizing: border-box;
    resize: vertical;
    transition: border-color 0.3s;
    &:focus {
        border-color: #c5705d;
        outline: none;
    }
`;

const IngredientGroup = styled.div`
    display: flex;
    gap: 10px;
    align-items: center;
    margin-bottom: 20px;
    position: relative;
`;

const Input = styled.input`
    padding: 12px;
    border-radius: 5px;
    border: 1px solid ${props => (props.error ? '#d9534f' : '#ccc')};
    font-size: 16px;
    width: 150px;
    transition: border-color 0.3s;
    &:focus {
        border-color: ${props => (props.error ? '#d9534f' : '#c5705d')};
        outline: none;
    }
`;

const Select = styled.select`
    padding: 12px;
    border-radius: 5px;
    border: 1px solid #ccc;
    font-size: 16px;
    width: 150px;
    transition: border-color 0.3s;
    &:focus {
        border-color: #c5705d;
        outline: none;
    }
`;

const ErrorText = styled.span`
    color: #d9534f;
    font-size: 14px;
    position: absolute;
    top: 100%; /* Position error text below the input */
    left: 0;
    margin-top: 3px;
    white-space: nowrap;
    display: ${props => (props.show ? 'block' : 'none')};
`;

const ButtonGroup = styled.div`
    display: flex;
    gap: 10px;
    margin-top: 10px;
    justify-content: flex-end; /* Align buttons to the right of the boxes */
`;

const Button = styled.button`
    border-radius: 5px;
    padding: 10px 15px;
    font-size: 14px;
    font-family: 'Poppins', sans-serif;
    cursor: pointer;
    text-align: center;
    color: #FFFFFF;
    transition: background-color 0.3s;
    border: none;

    &.add {
        background-color: #5b9e5d;
    }

    &.add:hover {
        background-color: #3d493e;
    }

    &.delete {
        background-color: #de3a3ac8;
    }

    &.delete:hover {
        background-color: #7b5852;
    }

    &.substitute {
        background-color: #f0ad4e;
    }

    &.substitute:hover {
        background-color: #ec971f;
    }
`;

const SubmitButton = styled.button`
    width: 100%;
    padding: 15px;
    background-color: #c5705d;
    color: #FFFFFF;
    border: none;
    border-radius: 8px;
    cursor: pointer;
    font-size: 16px;
    font-family: 'Poppins', sans-serif;
    font-weight: bold;
    transition: background-color 0.3s;
    text-align: center;

    &:hover {
        background-color: #ab886d;
    }
`;

const Title = styled.h2`
    text-align: center;
    margin-bottom: 30px;
    font-family: 'EB Garamond', serif;
    color: #d77a65;
    font-size: 32px;
    font-weight: bold;
`;

const AddRecipePage = () => {
    const [formErrors, setFormErrors] = useState({});
    const [showSubstituteInput, setShowSubstituteInput] = useState(false);

    const validateField = (name, value) => {
        let error = '';
        if (name === 'recipeName' && value && !/^[A-Za-z\s]+$/.test(value)) {
            error = 'Only letters and spaces allowed';
        } else if (name === 'quantity' && value && !/^\d+$/.test(value)) {
            error = 'Only numbers allowed';
        } else if (name === 'ingredient' && value && !/^[A-Za-z\s]+$/.test(value)) {
            error = 'Only letters are allowed';
        }
        setFormErrors(prevErrors => ({ ...prevErrors, [name]: error }));
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        validateField(name, value);
    };

    const handleToggleSubstitute = () => {
        setShowSubstituteInput(!showSubstituteInput);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (Object.values(formErrors).every(error => !error)) {
            console.log('Form submitted successfully!');
        }
    };

    return (
        <PageWrapper>
            <Title>Add a New Recipe</Title>
            <Form onSubmit={handleSubmit}>
                <FormGroup>
                    <Label htmlFor="recipeName">Recipe Name:</Label>
                    <Input
                        type="text"
                        id="recipeName"
                        name="recipeName"
                        onChange={handleInputChange}
                        error={!!formErrors.recipeName}
                    />
                    <ErrorText show={!!formErrors.recipeName}>{formErrors.recipeName}</ErrorText>
                </FormGroup>

                <FormGroup>
                    <Label htmlFor="description">Description:</Label>
                    <FullWidthTextarea
                        id="description"
                        placeholder="Add a short description of the recipe"
                    />
                </FormGroup>

                <Label>Ingredients</Label>
                <IngredientGroup>
                    <Input
                        type="text"
                        placeholder="Quantity"
                        name="quantity"
                        onChange={handleInputChange}
                        error={!!formErrors.quantity}
                    />
                    <Select>
                        <option>Select Measure</option>
                        <option>mg</option>
                        <option>g</option>
                        <option>kg</option>
                        <option>oz</option>
                        <option>lb</option>
                        <option>ml</option>
                        <option>liter</option>
                        <option>cup</option>
                        <option>tbsp</option>
                        <option>tsp</option>
                        <option>fl oz</option>
                        <option>quart</option>
                        <option>pint</option>
                        <option>unit</option>
                        <option>some</option>
                    </Select>
                    <Input
                        type="text"
                        placeholder="Ingredient"
                        name="ingredient"
                        onChange={handleInputChange}
                        error={!!formErrors.ingredient}
                    />
                    <ErrorText show={!!formErrors.quantity}>{formErrors.quantity}</ErrorText>
                    <ErrorText show={!!formErrors.ingredient}>{formErrors.ingredient}</ErrorText>
                </IngredientGroup>

                {showSubstituteInput && (
                    <IngredientGroup>
                        <Input
                            type="text"
                            placeholder="Substitute Ingredient"
                            name="substituteIngredient"
                        />
                    </IngredientGroup>
                )}

                <ButtonGroup>
                    <Button type="button" className="add">+ Add Ingredient</Button>
                    <Button type="button" className="delete">- Delete</Button>
                    <Button
                        type="button"
                        className="substitute"
                        onClick={handleToggleSubstitute}
                    >
                        {showSubstituteInput ? '- Delete Substitute' : '+ Substitute'}
                    </Button>
                </ButtonGroup>

                <Label>Instructions</Label>
                <FormGroup>
                    <FullWidthTextarea placeholder="Step 1" />
                </FormGroup>

                <ButtonGroup>
                    <Button type="button" className="add">+ Add Step</Button>
                    <Button type="button" className="delete">- Delete</Button>
                </ButtonGroup>

                <FormGroup>
                    <Label htmlFor="photo">Photo:</Label>
                    <Input type="file" id="photo" name="photo" />
                </FormGroup>

                <SubmitButton type="submit">Submit Recipe</SubmitButton>
            </Form>
        </PageWrapper>
    );
};

export default AddRecipe
