import React from 'react';
import { Link } from 'react-router-dom';
import logo from './logo.png'; // Import your logo image

const globalStyles = {
  htmlBody: {
    margin: 0,
    padding: 0,
    width: '100%',
    overflowX: 'hidden', // Prevent horizontal scrolling
    boxSizing: 'border-box',
  },
};

const styles = {
  navbar: {
    backgroundColor: '#D6C0B3',
    display: 'flex',
    alignItems: 'center',
    padding: '15px 20px',
    width: '100%',
    fontFamily: "'Poppins', sans-serif",
    borderBottom: '2px solid #D4AF37',
    boxSizing: 'border-box', // Ensure padding/borders are included in width
    overflowX: 'hidden', // Prevent horizontal scrolling
  },
  navbarLogo: {
    marginRight: 'auto', // Push logo to the far left
  },
  logoImage: {
    width: '120px',
  },
  navbarContent: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'flex-end',
    flexGrow: 1, // Push content to the right
    gap: '20px',
  },
  navbarLink: {
    textDecoration: 'none',
    color: '#F9F5F0',
    fontSize: '1rem',
    padding: '5px 10px',
    fontFamily: "'Poppins', sans-serif",
  },
  searchBar: {
    display: 'flex',
    alignItems: 'center',
    gap: '5px',
  },
  searchInput: {
    padding: '5px',
    fontSize: '1rem',
    borderRadius: '5px',
    border: '1px solid #D4AF37',
    backgroundColor: '#F9F5F0',
    color: '#333333',
  },
  searchButton: {
    padding: '5px 10px',
    backgroundColor: '#D4AF37',
    color: '#333333',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
};

function Navbar({ isLoggedIn, setIsLoggedIn }) {
  React.useEffect(() => {
    // Apply global styles
    Object.keys(globalStyles.htmlBody).forEach((key) => {
      document.body.style[key] = globalStyles.htmlBody[key];
      document.documentElement.style[key] = globalStyles.htmlBody[key];
    });
  }, []);

  return (
    <nav style={styles.navbar}>
      {/* Logo Section */}
      <div style={styles.navbarLogo}>
        <Link to="/">
          <img src={logo} alt="Culinareat Logo" style={styles.logoImage} />
        </Link>
      </div>

      {/* Navbar Content */}
      <div style={styles.navbarContent}>
        <Link to="/register" style={styles.navbarLink}>
          Register
        </Link>
        <Link to="/login" style={styles.navbarLink}>
          Login
        </Link>
        <Link to="/add-recipe" style={styles.navbarLink}>
          Add Recipe
        </Link>
        <div style={styles.searchBar}>
          <input
            type="text"
            placeholder="Search recipes..."
            style={styles.searchInput}
          />
          <button type="submit" style={styles.searchButton}>
            Search
          </button>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
