import React, { useState } from 'react';
import styled from 'styled-components';

const PageWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 40px;
  min-height: 100vh;
  background-color: #F4EDE3;
  font-family: 'Raleway', sans-serif;
`;

const Form = styled.form`
  width: 100%;
  max-width: 600px;
  padding: 20px;
  background-color: #FFF;
  box-shadow: 0px 4px 12px rgba(0, 0, 0, 0.1);
  border-radius: 10px;
`;

const FormGroup = styled.div`
  margin-bottom: 20px;
  display: flex;
  flex-direction: column;
  position: relative;
`;

const Label = styled.label`
  margin-bottom: 8px;
  font-size: 16px;
  color: #4a4a4a;
  font-family: 'Poppins', sans-serif;
  font-weight: 600;
`;

const Input = styled.input`
  padding: 12px 15px;
  border-radius: 5px;
  border: 1px solid #ddd;
  font-size: 16px;
  width: 100%;
  transition: border-color 0.3s;
  background-color: #f9f9f9;
  &:focus {
    border-color: #A77A58;
    outline: none;
    background-color: #fff;
  }
`;

const FullWidthTextarea = styled.textarea`
  width: 100%;
  padding: 12px 15px;
  border-radius: 5px;
  border: 1px solid #ddd;
  font-size: 16px;
  box-sizing: border-box;
  resize: vertical;
  background-color: #f9f9f9;
  transition: border-color 0.3s;
  &:focus {
    border-color: #A77A58;
    outline: none;
    background-color: #fff;
  }
`;

const IngredientGroup = styled.div`
  display: flex;
  gap: 10px;
  align-items: center;
  margin-bottom: 20px;
  position: relative;
`;

const Select = styled.select`
  padding: 12px 15px;
  border-radius: 5px;
  border: 1px solid #ddd;
  font-size: 16px;
  width: 100%;
  background-color: #f9f9f9;
  transition: border-color 0.3s;
  &:focus {
    border-color: #A77A58;
    outline: none;
    background-color: #fff;
  }
`;

const Button = styled.button`
  border-radius: 5px;
  padding: 10px 15px;
  font-size: 14px;
  font-family: 'Poppins', sans-serif;
  cursor: pointer;
  text-align: center;
  color: #FFFFFF;
  transition: background-color 0.3s;
  border: none;
  box-shadow: 0px 2px 5px rgba(0, 0, 0, 0.2);

  &.add {
    background-color: #4CAF50;
  }

  &.add:hover {
    background-color: #45a049;
  }

  &.delete {
    background-color: #f44336;
  }

  &.delete:hover {
    background-color: #da190b;
  }

  &.substitute {
    background-color: #ff9800;
  }

  &.substitute:hover {
    background-color: #e68900;
  }
`;

const SubmitButton = styled.button`
  width: 100%;
  padding: 15px;
  background-color: #A77A58;
  color: #FFFFFF;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  font-size: 16px;
  font-family: 'Poppins', sans-serif;
  font-weight: bold;
  transition: background-color 0.3s;
  text-align: center;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.2);

  &:hover {
    background-color: #8f6646;
  }
`;

const Title = styled.h2`
  text-align: center;
  margin-bottom: 30px;
  font-family: 'EB Garamond', serif;
  color: #A77A58;
  font-size: 32px;
  font-weight: bold;
`;

const Subtitle = styled.h3`
  font-family: 'Poppins', sans-serif;
  color: #4a4a4a;
  font-size: 18px;
  font-weight: bold;
  margin-top: 20px;
  margin-bottom: 10px;
`;

const AddRecipePage = () => {
  const [recipeName, setRecipeName] = useState("");
  const [description, setDescription] = useState("");
  const [ingredients, setIngredients] = useState([{ quantity: "", measure: "", name: "" }]);
  const [instructions, setInstructions] = useState([""]);

  const handleAddIngredient = () => setIngredients([...ingredients, { quantity: "", measure: "", name: "" }]);
  const handleDeleteIngredient = (index) => setIngredients(ingredients.filter((_, i) => i !== index));

  const handleAddStep = () => setInstructions([...instructions, ""]);
  const handleDeleteStep = (index) => setInstructions(instructions.filter((_, i) => i !== index));

  const handleFormSubmit = (e) => {
    e.preventDefault();
    const recipeData = {
      recipeName,
      description,
      ingredients,
      instructions,
    };
    console.log("Recipe Data:", recipeData);
    // Additional form handling logic
  };

  return (
    <PageWrapper>
      <Title>Add a New Recipe</Title>
      <Form onSubmit={handleFormSubmit}>
        <FormGroup>
          <Label htmlFor="recipeName">Recipe Name:</Label>
          <Input
            type="text"
            id="recipeName"
            value={recipeName}
            onChange={(e) => setRecipeName(e.target.value)}
          />
        </FormGroup>

        <FormGroup>
          <Label htmlFor="description">Description:</Label>
          <FullWidthTextarea
            id="description"
            placeholder="Add a short description of the recipe"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
        </FormGroup>

        <Subtitle>Ingredients</Subtitle>
        {ingredients.map((ingredient, index) => (
          <IngredientGroup key={index}>
            <Input
              type="text"
              placeholder="Quantity"
              value={ingredient.quantity}
              onChange={(e) => {
                const newIngredients = [...ingredients];
                newIngredients[index].quantity = e.target.value;
                setIngredients(newIngredients);
              }}
            />
            <Select
              value={ingredient.measure}
              onChange={(e) => {
                const newIngredients = [...ingredients];
                newIngredients[index].measure = e.target.value;
                setIngredients(newIngredients);
              }}
            >
              <option value="">Select Measure</option>
              <option value="g">g</option>
              <option value="kg">kg</option>
              <option value="oz">oz</option>
              <option value="ml">ml</option>
              <option value="cup">Cup</option>
              <option value="tbsp">Tbsp</option>
              <option value="tsp">Tsp</option>
            </Select>
            <Input
              type="text"
              placeholder="Ingredient"
              value={ingredient.name}
              onChange={(e) => {
                const newIngredients = [...ingredients];
                newIngredients[index].name = e.target.value;
                setIngredients(newIngredients);
              }}
            />
            <Button className="delete" onClick={() => handleDeleteIngredient(index)}>
              - Delete
            </Button>
          </IngredientGroup>
        ))}
        <Button className="add" onClick={handleAddIngredient}>+ Add Ingredient</Button>

        <Subtitle>Instructions</Subtitle>
        {instructions.map((step, index) => (
          <FormGroup key={index}>
            <FullWidthTextarea
              placeholder={`Step ${index + 1}`}
              value={step}
              onChange={(e) => {
                const newInstructions = [...instructions];
                newInstructions[index] = e.target.value;
                setInstructions(newInstructions);
              }}
            />
            <Button className="delete" onClick={() => handleDeleteStep(index)}>
              - Delete
            </Button>
          </FormGroup>
        ))}
        <Button className="add" onClick={handleAddStep}>+ Add Step</Button>

        <FormGroup>
          <Label htmlFor="photo">Photo:</Label>
          <Input type="file" id="photo" />
        </FormGroup>

        <SubmitButton type="submit">Submit Recipe</SubmitButton>
      </Form>
    </PageWrapper>
  );
};

export default AddRecipePage;
