import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const styles = {
  loginPage: {
    display: 'flex',
    height: '100vh',
    width: '100vw',
    flexDirection: 'row-reverse',
  },
  rightImageContainer: {
    flex: 1,
    position: 'relative',
    backgroundImage: `url('../images/img4.png')`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  overlayText: {
    position: 'absolute',
    color: '#ffffff',
    fontSize: '2rem',
    textAlign: 'center',
    padding: '30px',
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    borderRadius: '10px',
    fontFamily: "'Dancing Script', cursive",
    boxShadow: '0px 4px 10px rgba(0, 0, 0, 0.7)',
    letterSpacing: '1px',
    lineHeight: '1.5',
    textShadow: '2px 2px 8px rgba(0, 0, 0, 0.8)',
    transition: 'transform 0.3s ease-in-out, opacity 0.3s ease-in-out',
    opacity: 0.9,
    maxWidth: '90%',
    animation: 'fadeIn 1s ease-out',
  },
  leftFormContainer: {
    flex: 1,
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#fff',
    padding: '50px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
  },
  formContainer: {
    width: '100%',
    maxWidth: '400px',
    textAlign: 'center',
  },
  formHeading: {
    fontSize: '28px',
    color: '#333',
    marginBottom: '20px',
  },
  formGroup: {
    position: 'relative',
    marginBottom: '20px',
  },
  label: {
    textAlign: 'left',
    display: 'block',
    marginBottom: '5px',
    fontSize: '14px',
    color: '#777',
  },
  input: {
    width: '100%',
    padding: '12px',
    borderRadius: '5px',
    border: '1px solid #ccc',
    fontSize: '16px',
    boxSizing: 'border-box',
  },
  submitButton: {
    width: '100%',
    padding: '12px',
    backgroundColor: '#4397c1',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    fontSize: '16px',
    marginTop: '20px',
    transition: 'background-color 0.3s ease',
  },
  submitButtonHover: {
    backgroundColor: '#357a94',
  },
  error: {
    color: '#fff',
    backgroundColor: 'rgba(0, 0, 0, 0.7)',
    padding: '5px 10px',
    borderRadius: '8px',
    position: 'absolute',
    top: '50px',
    left: '0',
    width: 'calc(100% - 20px)',
    textAlign: 'left',
    display: 'flex',
    alignItems: 'center',
    marginTop: '5px',
    fontSize: '0.75em',
    zIndex: 10,
  },
  errorBanner: {
    color: '#fff',
    backgroundColor: 'rgba(217, 83, 79, 0.7)',
    padding: '12px',
    borderRadius: '12px',
    marginTop: '20px',
    fontSize: '16px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    textAlign: 'center',
    width: '25.3%',
    position: 'absolute',
    left: '60%',
    boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.2)',
    animation: 'bounce-in 0.6s ease-in-out',
  },
};

function LoginPage({ setIsLoggedIn }) {
  const [formData, setFormData] = useState({
    UserName: '',
    Password: '',
  });

  const [errors, setErrors] = useState({});
  const [apiErrorMessage, setApiErrorMessage] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const validate = () => {
    let inputErrors = {};
    if (!formData.UserName) {
      inputErrors.UserName = 'Username is required';
    }
    if (!formData.Password) {
      inputErrors.Password = 'Password is required';
    }
    return inputErrors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const inputErrors = validate();

    if (Object.keys(inputErrors).length === 0) {
      try {
        const response = await axios.post('http://localhost:5001/login', formData, {
          headers: {
            'Content-Type': 'application/json',
          },
          withCredentials: true,
        });

        setIsLoggedIn(true);
        localStorage.setItem('isLoggedIn', 'true');
        localStorage.setItem('userName', formData.UserName);
        setApiErrorMessage('');
        navigate('/');
      } catch (error) {
        if (error.response && error.response.status === 401) {
          setApiErrorMessage('Invalid username or password.');
        } else {
          setApiErrorMessage('An error occurred during login. Please try again.');
          console.error('Login error:', error);
        }
      }
    } else {
      setErrors(inputErrors);
    }
  };

  return (
    <div style={styles.loginPage}>
      <div style={styles.leftFormContainer}>
        <div style={styles.formContainer}>
          <h2 style={styles.formHeading}>Login to CulinarEAT</h2>
          <form onSubmit={handleSubmit}>
            <div style={styles.formGroup}>
              <label htmlFor="UserName" style={styles.label}>
                Username:
              </label>
              <input
                type="text"
                id="UserName"
                name="UserName"
                value={formData.UserName}
                onChange={handleChange}
                style={styles.input}
                required
              />
              {errors.UserName && <div style={styles.error}>{errors.UserName}</div>}
            </div>
            <div style={styles.formGroup}>
              <label htmlFor="Password" style={styles.label}>
                Password:
              </label>
              <input
                type="password"
                id="Password"
                name="Password"
                value={formData.Password}
                onChange={handleChange}
                style={styles.input}
                required
              />
              {errors.Password && <div style={styles.error}>{errors.Password}</div>}
            </div>
            <button type="submit" style={styles.submitButton}>
              Login
            </button>
          </form>
          {apiErrorMessage && <div style={styles.errorBanner}>{apiErrorMessage}</div>}
        </div>
      </div>
      <div style={styles.rightImageContainer}>
        <div style={styles.overlayText}>
          Glad to see you back! <br />
          Login to continue your culinary journey.
        </div>
      </div>
    </div>
  );
}

export default LoginPage;
