import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import axios from 'axios';

const PageWrapper = styled.div`
  display: flex;
  flex-direction: row-reverse;
  height: 100vh;
  width: 100vw;
  background: linear-gradient(135deg, #e9e4e0 0%, #f3ece8 100%);
`;

const RightImageContainer = styled.div`
  flex: 1;
  position: relative;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const OverlayText = styled.div`
  position: absolute;
  top: 20%;
  color: #ffffff;
  font-size: 2rem;
  text-align: center;
  padding: 30px;
  background-color: rgba(0, 0, 0, 0.5);
  border-radius: 10px;
  font-family: 'Dancing Script', cursive;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.7);
  letter-spacing: 1px;
  line-height: 1.5;
  text-shadow: 2px 2px 8px rgba(0, 0, 0, 0.8);
`;

const LeftFormContainer = styled.div`
  flex: 1;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const FormContainer = styled.form`
  width: 100%;
  max-width: 400px;
  text-align: center;
  padding: 30px;
  border-radius: 15px;
  background: linear-gradient(135deg, #ffffff 0%, #f5f5f5 100%);
  box-shadow: 0px 8px 24px rgba(0, 0, 0, 0.2);
`;

const Logo = styled.h1`
  font-family: 'Raleway', sans-serif;
  font-size: 2.5rem;
  color: #8b5e3c;
  margin-bottom: 20px;
`;

const WelcomeText = styled.h2`
  font-size: 1.5rem;
  font-weight: 600;
  color: #333;
  margin-bottom: 5px;
`;

const SubtitleText = styled.p`
  font-size: 1rem;
  color: #777;
  margin-bottom: 20px;
`;

const FormGroup = styled.div`
  margin-bottom: 20px;
  position: relative;
`;

const Label = styled.label`
  text-align: left;
  font-size: 14px;
  color: #777;
  margin-bottom: 5px;
  display: block;
`;

const Input = styled.input`
  width: 100%;
  padding: 12px 40px 12px 12px; /* Space for the checkmark */
  border-radius: 5px;
  border: 1px solid ${({ isError }) => (isError ? '#e74c3c' : '#ccc')};
  font-size: 16px;
  box-sizing: border-box;
  transition: border-color 0.3s, box-shadow 0.3s;

  &:focus {
    border-color: ${({ isError }) => (isError ? '#e74c3c' : '#5b9e5d')};
    box-shadow: 0 0 8px rgba(92, 158, 93, 0.3);
    outline: none;
  }
`;

const Checkmark = styled.span`
  position: absolute;
  top: 50%;
  right: 12px;
  transform: translateY(-50%);
  font-size: 1.5rem;
  color: ${({ isValid }) => (isValid ? '#5b9e5d' : 'transparent')};
`;

const ForgotPasswordLink = styled.a`
  font-size: 14px;
  font-weight: bold;
  color: #8b5e3c;
  text-decoration: none;
  cursor: pointer;
  margin-left: auto;
  display: block;
  transition: color 0.3s ease;

  &:hover {
    color: #70462c;
  }
`;

const RememberMeContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: 20px;
`;

const RememberMeLabel = styled.label`
  font-size: 14px;
  color: #333;
  display: flex;
  align-items: center;
  gap: 5px;
`;

const Checkbox = styled.input`
  width: 16px;
  height: 16px;
`;

const SubmitButton = styled.button`
  width: 100%;
  padding: 12px;
  background-color: #8b5e3c;
  color: white;
  border: none;
  border-radius: 12px;
  cursor: pointer;
  font-size: 16px;
  transition: background-color 0.3s, transform 0.2s;

  &:hover {
    background-color: #70462c;
    transform: translateY(-3px);
  }
`;

const ErrorText = styled.div`
  color: #e74c3c;
  font-size: 14px;
  margin-top: 5px;
  min-height: 20px;
  visibility: ${({ isVisible }) => (isVisible ? 'visible' : 'hidden')};
`;

const SignUpLink = styled.p`
  font-size: 14px;
  margin-top: 20px;
  color: #777;

  a {
    font-weight: bold;
    color: #8b5e3c;
    text-decoration: none;

    &:hover {
      color: #70462c;
    }
  }
`;

function LoginPage({ setIsLoggedIn }) {
  const [formData, setFormData] = useState({
    UserName: '',
    Password: '',
  });
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const validateUsername = (value) => (!value ? 'Username is required.' : '');

  const validatePassword = (value) =>
    !value ? 'Password is required.' : value.length < 6 ? 'Password must be at least 6 characters.' : '';

  const handleChange = (e) => {
    const { name, value } = e.target;

    setFormData((prevData) => ({ ...prevData, [name]: value }));

    if (name === 'UserName') {
      setErrors((prevErrors) => ({
        ...prevErrors,
        UserName: validateUsername(value),
      }));
    }

    if (name === 'Password') {
      setErrors((prevErrors) => ({
        ...prevErrors,
        Password: validatePassword(value),
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const usernameError = validateUsername(formData.UserName);
    const passwordError = validatePassword(formData.Password);

    if (usernameError || passwordError) {
      setErrors({ UserName: usernameError, Password: passwordError });
      return;
    }

    try {
      await axios.post('http://localhost:5001/login', formData, {
        headers: { 'Content-Type': 'application/json' },
        withCredentials: true,
      });

      setIsLoggedIn(true);
      localStorage.setItem('isLoggedIn', 'true');
      localStorage.setItem('userName', formData.UserName);
      navigate('/');
    } catch {
      setErrors((prevErrors) => ({
        ...prevErrors,
        api: 'Invalid username or password.',
      }));
    }
  };

  return (
    <PageWrapper>
      <LeftFormContainer>
        <FormContainer onSubmit={handleSubmit}>
          <Logo>CulinarEAT</Logo>
          <WelcomeText>Welcome Back</WelcomeText>
          <SubtitleText>Sign in with your username and password</SubtitleText>
          <FormGroup>
            <Label htmlFor="UserName">Username</Label>
            <Input
              type="text"
              id="UserName"
              name="UserName"
              value={formData.UserName}
              onChange={handleChange}
              isError={!!errors.UserName}
              placeholder="Enter your username"
            />
            <Checkmark isValid={!errors.UserName && formData.UserName}>✔</Checkmark>
            <ErrorText isVisible={!!errors.UserName}>{errors.UserName}</ErrorText>
          </FormGroup>
          <FormGroup>
            <Label htmlFor="Password">Password</Label>
            <Input
              type="password"
              id="Password"
              name="Password"
              value={formData.Password}
              onChange={handleChange}
              isError={!!errors.Password}
              placeholder="Enter your password"
            />
            <Checkmark isValid={!errors.Password && formData.Password}>✔</Checkmark>
            <ErrorText isVisible={!!errors.Password}>{errors.Password}</ErrorText>
          </FormGroup>
          <RememberMeContainer>
            <RememberMeLabel>
              <Checkbox type="checkbox" /> Remember me
            </RememberMeLabel>
            <ForgotPasswordLink href="/forgot-password">Forgot Password?</ForgotPasswordLink>
          </RememberMeContainer>
          <SubmitButton type="submit">Sign In</SubmitButton>
          <ErrorText isVisible={!!errors.api}>{errors.api}</ErrorText>
          <SignUpLink>
            Don't have an account? <a href="/register">Sign Up</a>
          </SignUpLink>
        </FormContainer>
      </LeftFormContainer>
      <RightImageContainer>
        <OverlayText>
          Glad to see you back! <br />
          Login to continue your culinary journey.
        </OverlayText>
      </RightImageContainer>
    </PageWrapper>
  );
}

export default LoginPage;
