import React, { useState } from 'react';
import styled from 'styled-components';

// Styled Components
const PageWrapper = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    padding: 40px;
    min-height: 100vh;
    background-color: #D6C0B3;
    font-family: 'Raleway', sans-serif;
`;

const Form = styled.form`
    width: 100%;
    max-width: 600px;
    box-sizing: border-box;
`;

const FormGroup = styled.div`
    margin-bottom: 20px;
    display: flex;
    flex-direction: column;
    position: relative;
`;

const PhotoFormGroup = styled(FormGroup)`
    margin-top: 20px; /* Add extra space above "Photo" label */
`;

const Label = styled.label`
    margin-bottom: 8px;
    font-size: 16px;
    color: #6f6666;
    font-family: 'Poppins', sans-serif;
    font-weight: bold;
`;

const FullWidthTextarea = styled.textarea`
    width: 100%;
    padding: 12px;
    border-radius: 5px;
    border: 1px solid #ccc;
    font-size: 16px;
    box-sizing: border-box;
    resize: vertical;
    transition: border-color 0.3s;
    &:focus {
        border-color: #c5705d;
        outline: none;
    }
`;

const IngredientGroup = styled.div`
    display: flex;
    gap: 10px;
    align-items: center;
    margin-bottom: 20px;
    position: relative;
`;

const Input = styled.input`
    padding: 12px;
    border-radius: 5px;
    border: 1px solid ${props => (props.error ? '#d9534f' : '#ccc')};
    font-size: 16px;
    width: 100%;
    transition: border-color 0.3s;
    &:focus {
        border-color: ${props => (props.error ? '#d9534f' : '#c5705d')};
        outline: none;
    }
`;

const Select = styled.select`
    padding: 12px;
    border-radius: 5px;
    border: 1px solid #ccc;
    font-size: 16px;
    width: 100%;
    transition: border-color 0.3s;
    &:focus {
        border-color: #c5705d;
        outline: none;
    }
`;

const ErrorText = styled.span`
    color: #d9534f;
    font-size: 14px;
    position: absolute;
    top: 100%; /* Position error text below the input */
    left: 0;
    margin-top: 3px;
    white-space: nowrap;
    display: ${props => (props.show ? 'block' : 'none')};
`;

const ButtonGroup = styled.div`
    display: flex;
    gap: 10px;
    margin-top: 10px;
`;

const Button = styled.button`
    border-radius: 5px;
    padding: 10px 15px;
    font-size: 14px;
    font-family: 'Poppins', sans-serif;
    cursor: pointer;
    text-align: center;
    color: #FFFFFF;
    transition: background-color 0.3s;
    border: none;

    &.add {
        background-color: #5b9e5d;
    }

    &.add:hover {
        background-color: #3d493e;
    }

    &.delete {
        background-color: #de3a3ac8;
    }

    &.delete:hover {
        background-color: #7b5852;
    }
`;

const SubmitButton = styled.button`
    width: 100%;
    padding: 15px;
    background-color: #c5705d;
    color: #FFFFFF;
    border: none;
    border-radius: 8px;
    cursor: pointer;
    font-size: 16px;
    font-family: 'Poppins', sans-serif;
    font-weight: bold;
    transition: background-color 0.3s;
    text-align: center;

    &:hover {
        background-color: #ab886d;
    }
`;

const Message = styled.div`
    margin-top: 20px;
    padding: 12px;
    border-radius: 5px;
    font-size: 14px;
    width: 100%;
    text-align: center;
    font-family: 'Poppins', sans-serif;
    color: ${props => (props.type === 'error' ? '#d9534f' : '#5b9e5d')};
    background-color: ${props => (props.type === 'error' ? '#f8d7da' : '#dff0d8')};
`;

const Title = styled.h2`
    text-align: center;
    margin-bottom: 30px;
    font-family: 'EB Garamond', serif;
    color: #d77a65;
    font-size: 32px;
    font-weight: bold;
`;

const Subtitle = styled.h3`
    font-family: 'Poppins', sans-serif;
    color: #6f6666;
    font-size: 18px;
    font-weight: bold;
    margin-top: 20px;
    margin-bottom: 10px;
`;

const AddRecipePage = () => {
    const [formErrors, setFormErrors] = useState({});
    const [successMessage, setSuccessMessage] = useState('');

    const validateField = (name, value) => {
        let error = '';
        if (name === 'recipeName' && value && !/^[A-Za-z\s]+$/.test(value)) {
            error = 'Recipe name should contain only letters and spaces.';
        } else if (name === 'quantity' && value && !/^\d+$/.test(value)) {
            error = 'Quantity should contain only numbers.';
        }
        setFormErrors(prevErrors => ({ ...prevErrors, [name]: error }));
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        validateField(name, value);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (Object.values(formErrors).every(error => !error)) {
            setSuccessMessage('Recipe added successfully!');
        } else {
            setSuccessMessage('');
        }
    };

    return (
        <PageWrapper>
            <Title>Add a New Recipe</Title>
            <Form onSubmit={handleSubmit}>
                <FormGroup>
                    <Label htmlFor="recipeName">Recipe Name:</Label>
                    <Input
                        type="text"
                        id="recipeName"
                        name="recipeName"
                        onChange={handleInputChange}
                        error={!!formErrors.recipeName}
                    />
                    <ErrorText show={!!formErrors.recipeName}>{formErrors.recipeName}</ErrorText>
                </FormGroup>

                <FormGroup>
                    <Label htmlFor="description">Description:</Label>
                    <FullWidthTextarea
                        id="description"
                        placeholder="Add a short description of the recipe"
                    />
                </FormGroup>

                <Subtitle>Ingredients</Subtitle>
                <IngredientGroup>
                    <Input
                        type="text"
                        placeholder="Quantity"
                        name="quantity"
                        onChange={handleInputChange}
                        error={!!formErrors.quantity}
                    />
                    <Select>
                        <option>Select Measure</option>
                        {/* Add more options here */}
                    </Select>
                    <Input type="text" placeholder="Ingredient" />
                    <ErrorText show={!!formErrors.quantity}>{formErrors.quantity}</ErrorText>
                </IngredientGroup>

                <ButtonGroup>
                    <Button type="button" className="add">+ Add Ingredient</Button>
                    <Button type="button" className="delete">- Delete</Button>
                </ButtonGroup>

                <Subtitle>Instructions</Subtitle>
                <FormGroup>
                    <FullWidthTextarea placeholder="Step 1" />
                </FormGroup>

                <ButtonGroup>
                    <Button type="button" className="add">+ Add Step</Button>
                    <Button type="button" className="delete">- Delete</Button>
                </ButtonGroup>

                <PhotoFormGroup>
                    <Label htmlFor="photo">Photo:</Label>
                    <Input type="file" id="photo" name="photo" />
                </PhotoFormGroup>

                <SubmitButton type="submit">Submit Recipe</SubmitButton>
                
                {successMessage && (
                    <Message type="success">{successMessage}</Message>
                )}
            </Form>
        </PageWrapper>
    );
};

export default AddRecipePage;
