import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import axios from 'axios';

const PageWrapper = styled.div`
  display: flex;
  flex-direction: row-reverse;
  height: 100vh;
  width: 100vw;
`;

const RightImageContainer = styled.div`
  flex: 1;
  position: relative;
  background-image: url('../images/img4.png');
  background-size: cover;
  background-position: center;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const OverlayText = styled.div`
  position: absolute;
  color: #ffffff;
  font-size: 2rem;
  text-align: center;
  padding: 30px;
  background-color: rgba(0, 0, 0, 0.5);
  border-radius: 10px;
  font-family: 'Dancing Script', cursive;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.7);
  letter-spacing: 1px;
  line-height: 1.5;
  text-shadow: 2px 2px 8px rgba(0, 0, 0, 0.8);
`;

const LeftFormContainer = styled.div`
  flex: 1;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: #fff;
  padding: 50px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
`;

const FormContainer = styled.form`
  width: 100%;
  max-width: 400px;
  text-align: center;
  padding: 30px;
  border-radius: 15px;
  background: #fff;
  box-shadow: 0px 8px 16px rgba(0, 0, 0, 0.1);
  border: 1px solid rgba(200, 200, 200, 0.3);
`;

const FormHeading = styled.h2`
  font-size: 28px;
  color: #333;
  margin-bottom: 20px;
  font-family: 'EB Garamond', serif;
`;

const FormGroup = styled.div`
  margin-bottom: 20px;
  position: relative;
  display: flex;
  flex-direction: column;
`;

const Label = styled.label`
  text-align: left;
  margin-bottom: 5px;
  font-size: 14px;
  color: #777;
`;

const Input = styled.input`
  width: 100%;
  padding: 12px;
  border-radius: 5px;
  border: 1px solid ${({ isError }) => (isError ? '#e74c3c' : '#ccc')};
  font-size: 16px;
  box-sizing: border-box;
  transition: border-color 0.3s, box-shadow 0.3s;

  &:focus {
    border-color: #5b9e5d;
    box-shadow: 0 0 8px rgba(92, 158, 93, 0.3);
    outline: none;
  }
`;

const SubmitButton = styled.button`
  width: 100%;
  padding: 12px;
  background-color: #8b5e3c;
  color: white;
  border: none;
  border-radius: 12px;
  cursor: pointer;
  font-size: 16px;
  margin-top: 20px;
  transition: background-color 0.3s, transform 0.2s;

  &:hover {
    background-color: #70462c;
    transform: translateY(-3px);
  }
`;

const ErrorText = styled.div`
  color: #e74c3c;
  background-color: rgba(231, 76, 60, 0.1);
  padding: 5px 10px;
  border-radius: 8px;
  margin-top: 5px;
  font-size: 14px;
`;

const ErrorBanner = styled.div`
  color: #fff;
  background-color: rgba(217, 83, 79, 0.7);
  padding: 12px;
  border-radius: 12px;
  margin-top: 20px;
  font-size: 16px;
  text-align: center;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.2);
`;

function LoginPage({ setIsLoggedIn }) {
  const [formData, setFormData] = useState({
    UserName: '',
    Password: '',
  });
  const [errors, setErrors] = useState({});
  const [apiErrorMessage, setApiErrorMessage] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const validate = () => {
    const inputErrors = {};
    if (!formData.UserName) {
      inputErrors.UserName = 'Username is required.';
    }
    if (!formData.Password) {
      inputErrors.Password = 'Password is required.';
    }
    return inputErrors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const inputErrors = validate();

    if (Object.keys(inputErrors).length === 0) {
      try {
        const response = await axios.post('http://localhost:5001/login', formData, {
          headers: {
            'Content-Type': 'application/json',
          },
          withCredentials: true,
        });

        setIsLoggedIn(true);
        localStorage.setItem('isLoggedIn', 'true');
        localStorage.setItem('userName', formData.UserName);
        setApiErrorMessage('');
        navigate('/');
      } catch (error) {
        if (error.response && error.response.status === 401) {
          setApiErrorMessage('Invalid username or password.');
        } else {
          setApiErrorMessage('An error occurred during login. Please try again.');
        }
      }
    } else {
      setErrors(inputErrors);
    }
  };

  return (
    <PageWrapper>
      <LeftFormContainer>
        <FormContainer onSubmit={handleSubmit}>
          <FormHeading>Login to CulinarEAT</FormHeading>
          <FormGroup>
            <Label htmlFor="UserName">Username</Label>
            <Input
              type="text"
              id="UserName"
              name="UserName"
              value={formData.UserName}
              onChange={handleChange}
              isError={!!errors.UserName}
            />
            {errors.UserName && <ErrorText>{errors.UserName}</ErrorText>}
          </FormGroup>
          <FormGroup>
            <Label htmlFor="Password">Password</Label>
            <Input
              type="password"
              id="Password"
              name="Password"
              value={formData.Password}
              onChange={handleChange}
              isError={!!errors.Password}
            />
            {errors.Password && <ErrorText>{errors.Password}</ErrorText>}
          </FormGroup>
          {apiErrorMessage && <ErrorBanner>{apiErrorMessage}</ErrorBanner>}
          <SubmitButton type="submit">Login</SubmitButton>
        </FormContainer>
      </LeftFormContainer>
      <RightImageContainer>
        <OverlayText>
          Glad to see you back! <br />
          Login to continue your culinary journey.
        </OverlayText>
      </RightImageContainer>
    </PageWrapper>
  );
}

export default LoginPage;
