import React, { useState } from 'react';
import styled from 'styled-components';

const PageWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 40px;
  min-height: 100vh;
  background-color: #D6C0B3;
  font-family: 'Raleway', sans-serif;
`;

const Form = styled.form`
  width: 100%;
  max-width: 600px;
  box-sizing: border-box;
`;

const FormGroup = styled.div`
  margin-bottom: 20px;
  display: flex;
  flex-direction: column;
  position: relative;
`;

const Label = styled.label`
  margin-bottom: 8px;
  font-size: 16px;
  color: #6f6666;
  font-family: 'Poppins', sans-serif;
  font-weight: bold;
`;

const Input = styled.input`
  padding: 12px;
  border-radius: 5px;
  border: 1px solid ${props => (props.error ? '#d9534f' : '#ccc')};
  font-size: 16px;
  width: 100%;
  transition: border-color 0.3s;
  &:focus {
    border-color: ${props => (props.error ? '#d9534f' : '#c5705d')};
    outline: none;
  }
`;

const FullWidthTextarea = styled.textarea`
  width: 100%;
  padding: 12px;
  border-radius: 5px;
  border: 1px solid #ccc;
  font-size: 16px;
  box-sizing: border-box;
  resize: vertical;
  transition: border-color 0.3s;
  &:focus {
    border-color: #c5705d;
    outline: none;
  }
`;

const IngredientGroup = styled.div`
  display: flex;
  gap: 10px;
  align-items: center;
  margin-bottom: 20px;
  position: relative;
`;

const Select = styled.select`
  padding: 12px;
  border-radius: 5px;
  border: 1px solid #ccc;
  font-size: 16px;
  width: 100%;
  transition: border-color 0.3s;
  &:focus {
    border-color: #c5705d;
    outline: none;
  }
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 10px;
  justify-content: flex-end;
  margin-top: 10px;
`;

const Button = styled.button`
  border-radius: 5px;
  padding: 10px 15px;
  font-size: 14px;
  font-family: 'Poppins', sans-serif;
  cursor: pointer;
  text-align: center;
  color: #FFFFFF;
  transition: background-color 0.3s;
  border: none;

  &.add {
    background-color: #5b9e5d;
  }

  &.add:hover {
    background-color: #3d493e;
  }

  &.delete {
    background-color: #de3a3ac8;
  }

  &.delete:hover {
    background-color: #7b5852;
  }

  &.substitute {
    background-color: #ffa726;
  }

  &.substitute:hover {
    background-color: #ff9800;
  }
`;

const SubmitButton = styled.button`
  width: 100%;
  padding: 15px;
  background-color: #c5705d;
  color: #FFFFFF;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  font-size: 16px;
  font-family: 'Poppins', sans-serif;
  font-weight: bold;
  transition: background-color 0.3s;
  text-align: center;

  &:hover {
    background-color: #ab886d;
  }
`;

const Title = styled.h2`
  text-align: center;
  margin-bottom: 30px;
  font-family: 'EB Garamond', serif;
  color: #d77a65;
  font-size: 32px;
  font-weight: bold;
`;

const Subtitle = styled.h3`
  font-family: 'Poppins', sans-serif;
  color: #6f6666;
  font-size: 18px;
  font-weight: bold;
  margin-top: 20px;
  margin-bottom: 10px;
`;

const AddRecipePage = () => {
  const [recipeName, setRecipeName] = useState("");
  const [description, setDescription] = useState("");
  const [ingredients, setIngredients] = useState([{ quantity: "", measure: "", name: "", substitute: "" }]);
  const [instructions, setInstructions] = useState([""]);

  const handleRecipeNameChange = (e) => setRecipeName(e.target.value);
  const handleDescriptionChange = (e) => setDescription(e.target.value);

  const handleIngredientChange = (index, field, value) => {
    const newIngredients = [...ingredients];
    newIngredients[index][field] = value;
    setIngredients(newIngredients);
  };

  const handleAddIngredient = () => {
    setIngredients([...ingredients, { quantity: "", measure: "", name: "", substitute: "" }]);
  };

  const handleDeleteIngredient = (index) => {
    setIngredients(ingredients.filter((_, i) => i !== index));
  };

  const handleAddStep = () => {
    setInstructions([...instructions, ""]);
  };

  const handleDeleteStep = (index) => {
    setInstructions(instructions.filter((_, i) => i !== index));
  };

  const handleStepChange = (index, value) => {
    const newInstructions = [...instructions];
    newInstructions[index] = value;
    setInstructions(newInstructions);
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    const recipeData = {
      recipeName,
      description,
      ingredients,
      instructions,
    };
    console.log("Recipe Data:", recipeData);
    // Additional form handling logic
  };

  return (
    <PageWrapper>
      <Title>Add a New Recipe</Title>
      <Form onSubmit={handleFormSubmit}>
        <FormGroup>
          <Label htmlFor="recipeName">Recipe Name:</Label>
          <Input
            type="text"
            id="recipeName"
            value={recipeName}
            onChange={handleRecipeNameChange}
          />
        </FormGroup>

        <FormGroup>
          <Label htmlFor="description">Description:</Label>
          <FullWidthTextarea
            id="description"
            placeholder="Add a short description of the recipe"
            value={description}
            onChange={handleDescriptionChange}
          />
        </FormGroup>

        <Subtitle>Ingredients</Subtitle>
        {ingredients.map((ingredient, index) => (
          <IngredientGroup key={index}>
            <Input
              type="text"
              placeholder="Quantity"
              value={ingredient.quantity}
              onChange={(e) => handleIngredientChange(index, 'quantity', e.target.value)}
            />
            <Select
              value={ingredient.measure}
              onChange={(e) => handleIngredientChange(index, 'measure', e.target.value)}
            >
              <option value="">Select Measure</option>
              <option value="mg">mg</option>
              <option value="g">g</option>
              <option value="kg">kg</option>
              <option value="oz">oz</option>
              <option value="lb">lb</option>
              <option value="ml">ml</option>
              <option value="liter">liter</option>
              <option value="cup">Cup</option>
              <option value="tbsp">Tbsp</option>
              <option value="tsp">Tsp</option>
              <option value="fl oz">fl oz</option>
              <option value="quart">Quart</option>
              <option value="pint">Pint</option>
              <option value="some">Some</option>
            </Select>
            <Input
              type="text"
              placeholder="Ingredient"
              value={ingredient.name}
              onChange={(e) => handleIngredientChange(index, 'name', e.target.value)}
            />
            <Button
              className="delete"
              onClick={() => handleDeleteIngredient(index)}
            >
              - Delete
            </Button>
          </IngredientGroup>
        ))}
        <Button className="add" onClick={handleAddIngredient}>+ Add Ingredient</Button>

        <Subtitle>Instructions</Subtitle>
        {instructions.map((step, index) => (
          <FormGroup key={index}>
            <FullWidthTextarea
              placeholder={`Step ${index + 1}`}
              value={step}
              onChange={(e) => handleStepChange(index, e.target.value)}
            />
            <Button className="delete" onClick={() => handleDeleteStep(index)}>
              - Delete
            </Button>
          </FormGroup>
        ))}
        <Button className="add" onClick={handleAddStep}>+ Add Step</Button>

        <FormGroup>
          <Label htmlFor="photo">Photo:</Label>
          <Input type="file" id="photo" />
        </FormGroup>

        <SubmitButton type="submit">Submit Recipe</SubmitButton>
      </Form>
    </PageWrapper>
  );
