import React, { useState } from 'react';
import styled from 'styled-components';

const PageWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 40px;
  min-height: 100vh;
  background: linear-gradient(135deg, #e9e4e0 0%, #f3ece8 100%);
  font-family: 'Raleway', sans-serif;
`;

const Form = styled.form`
  width: 100%;
  max-width: 780px;
  padding: 30px;
  border-radius: 15px;
  background: #fff;
  box-shadow: 0px 8px 16px rgba(0, 0, 0, 0.1);
  border: 1px solid rgba(200, 200, 200, 0.3);
`;

const FormGroup = styled.div`
  margin-bottom: 25px;
  display: flex;
  flex-direction: column;
  position: relative;
`;

const Title = styled.h2`
  text-align: center;
  margin-bottom: 30px;
  font-family: 'EB Garamond', serif;
  color: #333;
  font-size: 26px;
  font-weight: bold;
`;

const Label = styled.label`
  font-size: 16px;
  color: #333;
  font-weight: 600;
  display: flex;
  align-items: center;
  margin-bottom: 5px;
`;

const TagContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
  margin-top: 15px;
`;

const Tag = styled.div`
  padding: 8px 12px;
  border-radius: 15px;
  background-color: #f0a202;
  color: #fff;
  font-size: 14px;
  cursor: pointer;
  transition: background-color 0.3s;
  display: flex;
  align-items: center;
  gap: 8px;
  &:hover {
    background-color: #e68a00;
  }
`;

const Icon = styled.span`
  font-size: 14px;
`;

const SelectedTagsContainer = styled.div`
  padding: 12px;
  border: 1px solid #ccc;
  border-radius: 8px;
  margin-top: 15px;
  min-height: 50px;
  background-color: #f9f9f9;
`;

const SelectedTag = styled.div`
  display: inline-flex;
  align-items: center;
  gap: 8px;
  margin-right: 10px;
  padding: 8px 12px;
  border-radius: 15px;
  background-color: #5b9e5d;
  color: #fff;
  font-size: 14px;
  cursor: pointer;
`;

const SubmitButton = styled.button`
  border-radius: 12px;
  padding: 10px 16px;
  font-size: 16px;
  font-family: 'Raleway', sans-serif;
  cursor: pointer;
  color: #FFFFFF;
  border: none;
  background-color: #8B5E3C;
  transition: background-color 0.3s, transform 0.2s;
  &:hover {
    background-color: #70462c;
    transform: translateY(-3px);
  }
`;

const AddRecipePage = () => {
  const [recipeName, setRecipeName] = useState("");
  const [description, setDescription] = useState("");
  const [ingredients, setIngredients] = useState([{ quantity: "", measure: "", name: "", substitute: "", showSubstitute: false }]);
  const [instructions, setInstructions] = useState([""]);
  const [errors, setErrors] = useState({});
  const [showAddStepError, setShowAddStepError] = useState(false);
  const [selectedTags, setSelectedTags] = useState([]);

  const tags = [
    { label: "Kosher", icon: "🍞" },
    { label: "Not Kosher", icon: "🍖" },
    { label: "Meat", icon: "🥩" },
    { label: "Dairy", icon: "🧀" },
    { label: "Chicken", icon: "🍗" },
    { label: "Fish", icon: "🐟" },
    { label: "Desserts", icon: "🍰" },
    { label: "Gluten Free", icon: "🌾" },
    { label: "Lactose Free", icon: "🥛" },
    { label: "Vegan", icon: "🌱" },
    { label: "Vegetarian", icon: "🥕" },
    { label: "Nut Free", icon: "🥜" },
    { label: "Paleo", icon: "🥩" },
    { label: "Low Carb", icon: "🥦" },
    { label: "Low Fat", icon: "🍏" },
  ];

  const handleTagClick = (tag) => {
    setSelectedTags((prev) => [...prev, tag]);
  };

  const handleRemoveTag = (tag) => {
    setSelectedTags((prev) => prev.filter((t) => t.label !== tag.label));
  };

  return (
    <PageWrapper>
      <Form>
        <Title>Add a New Recipe</Title>

        {/* Recipe Name */}
        <FormGroup>
          <Label>Recipe Name</Label>
          <Input
            type="text"
            placeholder="Recipe Name"
            value={recipeName}
            onChange={(e) => setRecipeName(e.target.value)}
          />
        </FormGroup>

        {/* Description */}
        <FormGroup>
          <Label>Description</Label>
          <FullWidthTextarea
            placeholder="Description of the recipe"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
        </FormGroup>

        {/* Ingredients */}
        <FormGroup>
          <Label>Ingredients</Label>
          {ingredients.map((ingredient, index) => (
            <IngredientGroup key={index}>
              <Row>
                <SmallInputContainer>
                  <SmallInput
                    type="text"
                    placeholder="Quantity"
                    value={ingredient.quantity}
                    onChange={(e) => {
                      const newIngredients = [...ingredients];
                      newIngredients[index].quantity = e.target.value;
                      setIngredients(newIngredients);
                    }}
                  />
                </SmallInputContainer>
                
                <Select
                  value={ingredient.measure}
                  onChange={(e) => {
                    const newIngredients = [...ingredients];
                    newIngredients[index].measure = e.target.value;
                    setIngredients(newIngredients);
                  }}
                >
                  <option value="">Select Measure</option>
                  <option value="g">g</option>
                  <option value="kg">kg</option>
                  <option value="ml">ml</option>
                  <option value="cup">Cup</option>
                  <option value="tbsp">Tbsp</option>
                  <option value="tsp">Tsp</option>
                </Select>

                <SmallInputContainer>
                  <SmallInput
                    type="text"
                    placeholder="Ingredient"
                    value={ingredient.name}
                    onChange={(e) => {
                      const newIngredients = [...ingredients];
                      newIngredients[index].name = e.target.value;
                      setIngredients(newIngredients);
                    }}
                  />
                </SmallInputContainer>
                
                <DeleteButton
                  type="button"
                  onClick={() => {
                    const newIngredients = ingredients.filter((_, i) => i !== index);
                    setIngredients(newIngredients);
                  }}
                >
                  - Delete
                </DeleteButton>
              </Row>
            </IngredientGroup>
          ))}
          <AddButton
            type="button"
            onClick={() => setIngredients([...ingredients, { quantity: "", measure: "", name: "", substitute: "", showSubstitute: false }])}
          >
            + Add Another Ingredient
          </AddButton>
        </FormGroup>

        {/* Instructions */}
        <FormGroup>
          <Label>Instructions</Label>
          {instructions.map((step, index) => (
            <div key={index} style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
              <FullWidthTextarea
                placeholder={`Step ${index + 1}`}
                value={step}
                onChange={(e) => {
                  const newInstructions = [...instructions];
                  newInstructions[index] = e.target.value;
                  setInstructions(newInstructions);
                }}
              />
              <DeleteButton
                type="button"
                onClick={() => {
                  const newInstructions = instructions.filter((_, i) => i !== index);
                  setInstructions(newInstructions);
                }}
              >
                - Delete
              </DeleteButton>
            </div>
          ))}
          <AddButton
            type="button"
            onClick={() => setInstructions([...instructions, ""])}
          >
            + Add Step
          </AddButton>
        </FormGroup>

        {/* Labels Section */}
        <FormGroup>
          <Label>Labels (Select any that apply to your recipe):</Label>
          <TagContainer>
            {tags
              .filter((tag) => !selectedTags.includes(tag))
              .map((tag, index) => (
                <Tag key={index} onClick={() => handleTagClick(tag)}>
                  <Icon>{tag.icon}</Icon>
                  {tag.label}
                </Tag>
              ))}
          </TagContainer>

          <Label style={{ marginTop: '20px' }}>Selected Labels:</Label>
          <SelectedTagsContainer>
            {selectedTags.map((tag, index) => (
              <SelectedTag key={index} onClick={() => handleRemoveTag(tag)}>
                <Icon>{tag.icon}</Icon>
                {tag.label}
              </SelectedTag>
            ))}
          </SelectedTagsContainer>
        </FormGroup>

        {/* Submit Button */}
        <SubmitButton type="submit" onClick={() => alert("Recipe submitted!")}>
          Submit Recipe
        </SubmitButton>
      </Form>
    </PageWrapper>
  );
};

export default AddRecipePage;
