import React, { useState } from 'react';
import styled from 'styled-components';

// Styled Components
const PageWrapper = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    padding: 40px;
    min-height: 100vh;
    background-color: #D6C0B3;
    font-family: 'Raleway', sans-serif;
`;

const Form = styled.form`
    width: 100%;
    max-width: 600px;
    box-sizing: border-box;
`;

const FormGroup = styled.div`
    margin-bottom: 20px;
    display: flex;
    flex-direction: column;
    position: relative;
`;

const PhotoFormGroup = styled(FormGroup)`
    margin-top: 20px;
`;

const Label = styled.label`
    margin-bottom: 8px;
    font-size: 16px;
    color: #6f6666;
    font-family: 'Poppins', sans-serif;
    font-weight: bold;
`;

const FullWidthTextarea = styled.textarea`
    width: 100%;
    padding: 12px;
    border-radius: 5px;
    border: 1px solid #ccc;
    font-size: 16px;
    box-sizing: border-box;
    resize: vertical;
    transition: border-color 0.3s;
    &:focus {
        border-color: #c5705d;
        outline: none;
    }
`;

const IngredientGroup = styled.div`
    display: flex;
    gap: 10px;
    align-items: center;
    margin-bottom: 20px;
    position: relative;
`;

const Input = styled.input`
    padding: 12px;
    border-radius: 5px;
    border: 1px solid ${props => (props.error ? '#d9534f' : '#ccc')};
    font-size: 16px;
    flex: 1;
    transition: border-color 0.3s;
    &:focus {
        border-color: ${props => (props.error ? '#d9534f' : '#c5705d')};
        outline: none;
    }
`;

const Select = styled.select`
    padding: 12px;
    border-radius: 5px;
    border: 1px solid #ccc;
    font-size: 16px;
    flex: 1;
    transition: border-color 0.3s;
    &:focus {
        border-color: #c5705d;
        outline: none;
    }
`;

const ErrorText = styled.span`
    color: #d9534f;
    font-size: 14px;
    position: absolute;
    top: 100%;
    left: 0;
    margin-top: 3px;
    display: ${props => (props.show ? 'block' : 'none')};
`;

const ButtonGroup = styled.div`
    display: flex;
    gap: 10px;
    margin-top: 10px;
`;

const Button = styled.button`
    border-radius: 5px;
    padding: 10px 15px;
    font-size: 14px;
    font-family: 'Poppins', sans-serif;
    cursor: pointer;
    color: #FFFFFF;
    transition: background-color 0.3s;
    border: none;

    &.add { background-color: #5b9e5d; }
    &.add:hover { background-color: #3d493e; }
    &.delete { background-color: #de3a3ac8; }
    &.delete:hover { background-color: #7b5852; }
    &.substitute { background-color: #ffc107; color: #333; }
    &.substitute:hover { background-color: #e0a800; }
`;

const SubmitButton = styled.button`
    width: 100%;
    padding: 15px;
    background-color: #c5705d;
    color: #FFFFFF;
    border: none;
    border-radius: 8px;
    cursor: pointer;
    font-size: 16px;
    font-family: 'Poppins', sans-serif;
    font-weight: bold;
    transition: background-color 0.3s;

    &:hover { background-color: #ab886d; }
`;

const Message = styled.div`
    margin-top: 20px;
    padding: 12px;
    border-radius: 5px;
    font-size: 14px;
    width: 100%;
    text-align: center;
    font-family: 'Poppins', sans-serif;
    color: ${props => (props.type === 'error' ? '#d9534f' : '#5b9e5d')};
    background-color: ${props => (props.type === 'error' ? '#f8d7da' : '#dff0d8')};
`;

const Title = styled.h2`
    text-align: center;
    margin-bottom: 30px;
    font-family: 'EB Garamond', serif;
    color: #d77a65;
    font-size: 32px;
    font-weight: bold;
`;

const Subtitle = styled.h3`
    font-family: 'Poppins', sans-serif;
    color: #6f6666;
    font-size: 18px;
    font-weight: bold;
    margin-top: 20px;
    margin-bottom: 10px;
`;

const measurements = [
    "mg", "g", "kg", "oz", "lb", "ml", "liter", "cup", "Tbsp", "Tsp", "fl oz", "quart", "pint", "unit"
];

const AddRecipePage = () => {
    const [formErrors, setFormErrors] = useState({});
    const [successMessage, setSuccessMessage] = useState('');
    const [ingredients, setIngredients] = useState([{ quantity: '', measure: '', ingredient: '', substitute: '' }]);

    const validateField = (name, value, index) => {
        let error = '';
        if (name === 'recipeName' && value && !/^[A-Za-z\s]+$/.test(value)) {
            error = 'Recipe name should contain only letters and spaces.';
        } else if (name === 'quantity' && value && !/^\d+$/.test(value)) {
            error = 'Quantity should contain only numbers.';
        }
        setFormErrors(prevErrors => ({ ...prevErrors, [`${name}${index}`]: error }));
    };

    const handleInputChange = (e, index) => {
        const { name, value } = e.target;
        const updatedIngredients = [...ingredients];
        updatedIngredients[index][name] = value;
        setIngredients(updatedIngredients);
        validateField(name, value, index);
    };

    const handleToggleSubstitute = (index) => {
        const updatedIngredients = [...ingredients];
        updatedIngredients[index].showSubstitute = !updatedIngredients[index].showSubstitute;
        setIngredients(updatedIngredients);
    };

    const handleAddIngredient = () => {
        setIngredients([...ingredients, { quantity: '', measure: '', ingredient: '', substitute: '', showSubstitute: false }]);
    };

    const handleDeleteIngredient = (index) => {
        const newIngredients = ingredients.filter((_, i) => i !== index);
        setIngredients(newIngredients);
    };

    return (
        <PageWrapper>
            <Title>Add a New Recipe</Title>
            <Form onSubmit={(e) => e.preventDefault()}>
                <FormGroup>
                    <Label htmlFor="recipeName">Recipe Name:</Label>
                    <Input
                        type="text"
                        id="recipeName"
                        name="recipeName"
                        onChange={(e) => validateField("recipeName", e.target.value, 0)}
                        error={!!formErrors.recipeName0}
                    />
                    <ErrorText show={!!formErrors.recipeName0}>{formErrors.recipeName0}</ErrorText>
                </FormGroup>
                <Subtitle>Ingredients</Subtitle>
                {ingredients.map((ingredient, index) => (
                    <IngredientGroup key={index}>
                        <Input
                            type="text"
                            placeholder="Quantity"
                            name="quantity"
                            value={ingredient.quantity}
                            onChange={(e) => handleInputChange(e, index)}
                            error={!!formErrors[`quantity${index}`]}
                        />
                        <Select
                            name="measure"
                            value={ingredient.measure}
                            onChange={(e) => handleInputChange(e, index)}
                        >
                            <option value="">Select Measure</option>
                            {measurements.map((m) => <option key={m} value={m}>{m}</option>)}
                        </Select>
                        <Input
                            type="text"
                            placeholder="Ingredient"
                            name="ingredient"
                            value={ingredient.ingredient}
                            onChange={(e) => handleInputChange(e, index)}
                        />
                        {ingredient.showSubstitute && (
                            <Input
                                type="text"
                                placeholder="Substitute Ingredient"
                                name="substitute"
                                value={ingredient.substitute}
                                onChange={(e) => handleInputChange(e, index)}
                            />
                        )}
                        <ErrorText show={!!formErrors[`quantity${index}`]}>{formErrors[`quantity${index}`]}</ErrorText>
                        <ButtonGroup>
                            <Button type="button" className="add" onClick={handleAddIngredient}>+ Add Ingredient</Button>
                            <Button type="button" className="delete" onClick={() => handleDeleteIngredient(index)}>- Delete</Button>
                            <Button type="button" className="substitute" onClick={() => handleToggleSubstitute(index)}>
                                {ingredient.showSubstitute ? "- Delete Substitute" : "+ Substitute"}
                            </Button>
                        </ButtonGroup>
                    </IngredientGroup>
                ))}
                <SubmitButton type="submit">Submit Recipe</SubmitButton>
                {successMessage && <Message type="success">{successMessage}</Message>}
            </Form>
        </PageWrapper>
    );
};

export default AddRecipePage;
