import React, { useState } from 'react';
import styled from 'styled-components';

const PageWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 40px;
  min-height: 100vh;
  background-color: #D6C0B3;
  font-family: 'Raleway', sans-serif;
`;

const Form = styled.form`
  width: 100%;
  max-width: 800px;
  box-sizing: border-box;
`;

const FormGroup = styled.div`
  margin-bottom: 20px;
  display: flex;
  flex-direction: column;
  position: relative;
`;

const Label = styled.label`
  margin-bottom: 8px;
  font-size: 16px;
  color: #6f6666;
  font-family: 'Poppins', sans-serif;
  font-weight: bold;
`;

const Input = styled.input`
  padding: 12px;
  border-radius: 5px;
  border: 1px solid ${props => (props.error ? '#d9534f' : '#ccc')};
  font-size: 16px;
  width: 100%;
  transition: border-color 0.3s;
  max-width: 300px; /* Ensures Recipe Name aligns with other fields */
  &:focus {
    border-color: ${props => (props.error ? '#d9534f' : '#c5705d')};
    outline: none;
  }
`;

const FullWidthTextarea = styled.textarea`
  width: 100%;
  padding: 12px;
  border-radius: 5px;
  border: 1px solid #ccc;
  font-size: 16px;
  box-sizing: border-box;
  resize: vertical;
  transition: border-color 0.3s;
  &:focus {
    border-color: #c5705d;
    outline: none;
  }
`;

const IngredientGroup = styled.div`
  display: flex;
  align-items: center;
  gap: 10px;
  margin-bottom: 20px;
`;

const Select = styled.select`
  padding: 12px;
  border-radius: 5px;
  border: 1px solid #ccc;
  font-size: 16px;
  width: 100%;
  transition: border-color 0.3s;
  max-width: 100px;
  &:focus {
    border-color: #c5705d;
    outline: none;
  }
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 10px;
  margin-left: 10px;
`;

const Button = styled.button`
  border-radius: 5px;
  padding: 10px 15px;
  font-size: 14px;
  font-family: 'Poppins', sans-serif;
  cursor: pointer;
  text-align: center;
  color: #FFFFFF;
  transition: background-color 0.3s;
  border: none;

  &.add {
    background-color: #5b9e5d;
  }

  &.add:hover {
    background-color: #3d493e;
  }

  &.delete {
    background-color: #de3a3ac8;
  }

  &.delete:hover {
    background-color: #7b5852;
  }

  &.substitute {
    background-color: #ffa726;
  }

  &.substitute:hover {
    background-color: #ff9800;
  }
`;

const SubmitButton = styled.button`
  width: 100%;
  padding: 15px;
  background-color: #c5705d;
  color: #FFFFFF;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  font-size: 16px;
  font-family: 'Poppins', sans-serif;
  font-weight: bold;
  transition: background-color 0.3s;
  text-align: center;

  &:hover {
    background-color: #ab886d;
  }
`;

const Title = styled.h2`
  text-align: center;
  margin-bottom: 30px;
  font-family: 'EB Garamond', serif;
  color: #d77a65;
  font-size: 32px;
  font-weight: bold;
`;

const Subtitle = styled.h3`
  font-family: 'Poppins', sans-serif;
  color: #6f6666;
  font-size: 18px;
  font-weight: bold;
  margin-top: 20px;
  margin-bottom: 10px;
`;

const ErrorText = styled.span`
  color: #d9534f;
  font-size: 14px;
  position: absolute;
  top: 100%;
  left: 0;
  margin-top: 3px;
  white-space: nowrap;
  display: ${props => (props.show ? 'block' : 'none')};
`;

const AddRecipePage = () => {
  const [formErrors, setFormErrors] = useState({});
  const [showSubstitute, setShowSubstitute] = useState(false);

  const validateField = (name, value) => {
    let error = '';
    if (name === 'recipeName' && value && !/^[A-Za-z\s]+$/.test(value)) {
      error = 'Only letters are allowed';
    } else if (name === 'quantity' && value && !/^\d+$/.test(value)) {
      error = 'Only numbers are allowed';
    } else if (name === 'ingredient' && value && !/^[A-Za-z\s]+$/.test(value)) {
      error = 'Only letters are allowed';
    }
    setFormErrors(prevErrors => ({ ...prevErrors, [name]: error }));
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    validateField(name, value);
  };

  return (
    <PageWrapper>
      <Title>Add a New Recipe</Title>
      <Form>
        <FormGroup>
          <Label htmlFor="recipeName">Recipe Name:</Label>
          <Input
            type="text"
            id="recipeName"
            name="recipeName"
            onChange={handleInputChange}
            error={!!formErrors.recipeName}
          />
          <ErrorText show={!!formErrors.recipeName}>{formErrors.recipeName}</ErrorText>
        </FormGroup>

        <FormGroup>
          <Label htmlFor="description">Description:</Label>
          <FullWidthTextarea
            id="description"
            placeholder="Add a short description of the recipe"
          />
        </FormGroup>

        <Subtitle>Ingredients</Subtitle>
        <IngredientGroup>
          <Input
            type="text"
            placeholder="Quantity"
            name="quantity"
            onChange={handleInputChange}
            error={!!formErrors.quantity}
          />
          <Select name="measure">
            <option value="">Select Measure</option>
            <option value="mg">mg</option>
            <option value="g">g</option>
            <option value="kg">kg</option>
            <option value="oz">oz</option>
            <option value="lb">lb</option>
            <option value="ml">ml</option>
            <option value="liter">liter</option>
            <option value="cup">Cup</option>
            <option value="tbsp">Tbsp</option>
            <option value="tsp">Tsp</option>
            <option value="fl oz">fl oz</option>
            <option value="quart">Quart</option>
            <option value="pint">Pint</option>
            <option value="some">Some</option>
          </Select>
          <Input
            type="text"
            placeholder="Ingredient"
            name="ingredient"
            onChange={handleInputChange}
            error={!!formErrors.ingredient}
          />
          <ButtonGroup>
            <Button className="add">+ Add Ingredient</Button>
            <Button className="delete">- Delete</Button>
            <Button
              className="substitute"
              onClick={() => setShowSubstitute(!showSubstitute)}
            >
              {showSubstitute ? "- Substitute" : "+ Substitute"}
            </Button>
          </ButtonGroup>
          <ErrorText show={!!formErrors.ingredient}>{formErrors.ingredient}</ErrorText>
        </IngredientGroup>

        {showSubstitute && (
          <FormGroup>
            <Input
              type="text"
              placeholder="Substitute Ingredient"
              name="substitute"
            />
          </FormGroup>
        )}

        <Subtitle>Instructions</Subtitle>
        <FormGroup style={{ display: "flex", alignItems: "center", gap: "10px" }}>
          <FullWidthTextarea placeholder="Step 1" />
          <ButtonGroup>
            <Button className="add">+ Add Step</Button>
            <Button className="delete">- Delete</Button>
          </ButtonGroup>
        </FormGroup>

        <FormGroup>
          <Label htmlFor="photo">Photo:</Label>
          <Input type="file" id="photo" name="photo" />
        </FormGroup>

        <SubmitButton type="submit">Submit Recipe</SubmitButton>
      </Form>
    </PageWrapper>
  );
};

export default AddRecipePage;
