import React, { useState } from 'react';
import styled from 'styled-components';

const PageWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 40px;
  min-height: 100vh;
  background: linear-gradient(135deg, #e9e4e0 0%, #f3ece8 100%);
  font-family: 'Raleway', sans-serif;
`;

const Form = styled.form`
  width: 100%;
  max-width: 780px;
  padding: 30px;
  border-radius: 15px;
  background: #fff;
  box-shadow: 0px 8px 16px rgba(0, 0, 0, 0.1);
  border: 1px solid rgba(200, 200, 200, 0.3);
`;

const FormGroup = styled.div`
  margin-bottom: 25px;
  display: flex;
  flex-direction: column;
  position: relative;
`;

const Title = styled.h2`
  text-align: center;
  margin-bottom: 30px;
  font-family: 'EB Garamond', serif;
  color: #333;
  font-size: 26px;
  font-weight: bold;
`;

const Label = styled.label`
  font-size: 16px;
  color: #333;
  font-weight: 600;
  display: flex;
  align-items: center;
  margin-bottom: 5px;
  justify-content: center;
`;

const ErrorText = styled.span`
  color: #e74c3c;
  font-size: 14px;
  margin-top: 4px;
  height: 18px;
  visibility: ${({ isVisible }) => (isVisible ? 'visible' : 'hidden')};
`;

const Input = styled.input`
  padding: 12px 15px;
  border-radius: 8px;
  border: 1px solid rgba(0, 0, 0, 0.2);
  font-size: 16px;
  background-color: #fefefe;
  font-family: 'Raleway', sans-serif;
  transition: border-color 0.3s, box-shadow 0.3s;
  box-sizing: border-box;
  &:focus {
    border-color: #5b9e5d;
    box-shadow: 0px 0px 8px rgba(92, 158, 93, 0.3);
    outline: none;
  }
`;

const FullWidthTextarea = styled.textarea`
  padding: 12px 15px;
  border-radius: 8px;
  border: 1px solid rgba(0, 0, 0, 0.2);
  font-size: 16px;
  width: 100%;
  background-color: #fefefe;
  font-family: 'Raleway', sans-serif;
  resize: vertical;
  transition: border-color 0.3s, box-shadow 0.3s;
  box-sizing: border-box;
  &:focus {
    border-color: #5b9e5d;
    box-shadow: 0px 0px 8px rgba(92, 158, 93, 0.3);
    outline: none;
  }
`;

const IngredientGroup = styled.div`
  display: flex;
  flex-direction: column;
  gap: 10px;
  padding: 20px;
  border: 1px solid #ddd;
  border-radius: 10px;
  margin-bottom: 10px;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.05);
`;

const Row = styled.div`
  display: flex;
  gap: 10px;
  align-items: flex-start;
  justify-content: space-between;
  flex-wrap: nowrap;
`;

const SmallInputContainer = styled.div`
  display: flex;
  flex-direction: column;
  width: 35%;
`;

const SmallInput = styled(Input)`
  width: 100%;
`;

const Select = styled.select`
  padding: 12px 15px;
  border-radius: 8px;
  border: 1px solid rgba(0, 0, 0, 0.2);
  font-size: 16px;
  width: 35%;
  background-color: #fefefe;
  font-family: 'Raleway', sans-serif;
  transition: border-color 0.3s;
  box-sizing: border-box;
  &:focus {
    border-color: #5b9e5d;
    outline: none;
  }
`;

const Button = styled.button`
  border-radius: 12px;
  padding: 10px 16px;
  font-size: 16px;
  cursor: pointer;
  color: #FFFFFF;
  border: none;
  transition: background-color 0.3s, transform 0.2s;
  width: 100%;
  box-sizing: border-box;
  &:hover {
    transform: translateY(-3px);
  }
`;

const DeleteButton = styled(Button)`
  background-color: #ff5c5c;
  width: 80px;
  height: 48px;
  display: flex;
  align-items: center;
  justify-content: center;
  &:hover {
    background-color: #e64545;
  }
  &::before {
    content: '-';
    margin-right: 5px;
  }
`;

const AddButton = styled(Button)`
  background-color: #5b9e5d;
  &:hover {
    background-color: #4a8c50;
  }
  margin-bottom: 25px;
`;

const SubstituteButton = styled(Button)`
  background-color: #f0a202;
  &:hover {
    background-color: #e68a00;
  }
  width: auto;
`;

const SubmitButton = styled(Button)`
  background-color: #8B5E3C;
  &:hover {
    background-color: #70462c;
  }
`;

const CameraButton = styled.div`
  width: 80px;
  height: 80px;
  border: 2px solid #333;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  font-size: 32px;
  color: #333;
  background-color: #fff;
  transition: transform 0.3s, background-color 0.3s;
  &:hover {
    transform: scale(1.05);
    background-color: #f0f0f0;
  }
`;

const HiddenFileInput = styled.input`
  display: none;
`;

const TagContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
  margin-top: 15px;
`;

const Tag = styled.div`
  padding: 8px 12px;
  border-radius: 15px;
  background-color: #f0a202;
  color: #fff;
  font-size: 14px;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 8px;
`;

const SelectedTagsContainer = styled.div`
  padding: 12px;
  border: 1px solid #ccc;
  border-radius: 8px;
  margin-top: 15px;
  min-height: 50px;
  background-color: #f9f9f9;
`;

const SelectedTag = styled.div`
  display: inline-flex;
  align-items: center;
  gap: 8px;
  margin-right: 10px;
  padding: 8px 12px;
  border-radius: 15px;
  background-color: #5b9e5d;
  color: #fff;
  font-size: 14px;
  cursor: pointer;
`;

const CategoryContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
  margin-top: 15px;
  justify-content: center;
`;

const CategoryOption = styled.div`
  padding: 8px 16px;
  border-radius: 10px;
  background-color: #d1e8e2;
  color: #333;
  font-size: 14px;
  cursor: pointer;
  transition: background-color 0.3s;
  border: 1px solid #8B5E3C;
  &:hover {
    background-color: #b2d2ca;
  }
  &.selected {
    background-color: #8B5E3C;
    color: #fff;
  }
`;

const AddRecipePage = () => {
  const [recipeName, setRecipeName] = useState("");
  const [description, setDescription] = useState("");
  const [ingredients, setIngredients] = useState([{ quantity: "", measure: "", name: "", substitute: "", showSubstitute: false }]);
  const [instructions, setInstructions] = useState([""]);
  const [errors, setErrors] = useState({});
  const [showAddStepError, setShowAddStepError] = useState(false);
  const [selectedTags, setSelectedTags] = useState([]);
  const [selectedCategories, setSelectedCategories] = useState([]);
  const [file, setFile] = useState(null);

  const tags = [
    { label: "Kosher", icon: "ðŸž" },
    { label: "Not Kosher", icon: "ðŸ–" },
    { label: "Dairy", icon: "ðŸ§€" },
    { label: "Meat", icon: "ðŸ¥©" },
    { label: "Chicken", icon: "ðŸ—" },
    { label: "Fish", icon: "ðŸŸ" },
    { label: "Desserts", icon: "ðŸ°" },
    { label: "Vegan", icon: "ðŸŒ±" },
    { label: "Vegetarian", icon: "ðŸ¥•" },
    { label: "Nut Free", icon: "ðŸ¥œ" },
    { label: "Gluten Free", icon: "ðŸŒ¾" },
    { label: "Lactose Free", icon: "ðŸ¥›" },
    { label: "Paleo", icon: "ðŸ¥©" },
    { label: "Low Carb", icon: "ðŸ¥¦" },
    { label: "Low Fat", icon: "ðŸ" },
  ];

  const categories = [
    "Meals in 10 Minutes",
    "Holidays",
    "Breakfast",
    "Lunch",
    "Dinner",
    "Meals for Date",
    "Kids' Meals",
    "Vegetarian",
    "Vegan",
    "Gluten-Free"
  ];

  const handleTagClick = (tag) => {
    if (!selectedTags.find((t) => t.label === tag.label)) {
      setSelectedTags([...selectedTags, tag]);
    }
  };

  const handleRemoveTag = (tag) => {
    setSelectedTags(selectedTags.filter((t) => t.label !== tag.label));
  };

  const handleCategoryClick = (category) => {
    setSelectedCategories((prevCategories) =>
      prevCategories.includes(category)
        ? prevCategories.filter((c) => c !== category)
        : [...prevCategories, category]
    );
  };

  const handleFileChange = (event) => {
    const selectedFile = event.target.files[0];
    if (selectedFile) {
      setFile(selectedFile);
    }
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    alert("Recipe submitted successfully!");
  };

  return (
    <PageWrapper>
      <Form onSubmit={handleFormSubmit}>
        <Title>Add a New Recipe</Title>

        <FormGroup>
          <Label>Photo:</Label>
          <CameraButton onClick={() => document.getElementById('fileInput').click()}>
            ðŸ“·
          </CameraButton>
          <HiddenFileInput
            type="file"
            id="fileInput"
            onChange={handleFileChange}
          />
          {file && <p>Selected file: {file.name}</p>}
        </FormGroup>

        {/* Recipe Name */}
        <FormGroup>
          <Label htmlFor="recipeName">Recipe Name</Label>
          <Input
            type="text"
            id="recipeName"
            placeholder="E.g., Grandma's Apple Pie"
            value={recipeName}
            onChange={(e) => setRecipeName(e.target.value)}
            aria-label="Recipe name"
          />
          <ErrorText isVisible={!!errors.recipeName}>{errors.recipeName}</ErrorText>
        </FormGroup>

        {/* Description */}
        <FormGroup>
          <Label htmlFor="description">Description</Label>
          <FullWidthTextarea
            id="description"
            placeholder="Add a short description of the recipe"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            aria-label="Recipe description"
          />
          <ErrorText isVisible={!!errors.description}>{errors.description}</ErrorText>
        </FormGroup>

        {/* Ingredients */}
        <FormGroup>
          <Label>Ingredients</Label>
          {ingredients.map((ingredient, index) => (
            <IngredientGroup key={index}>
              <Row>
                <SmallInputContainer>
                  <SmallInput
                    type="text"
                    placeholder="Quantity"
                    value={ingredient.quantity}
                    onChange={(e) => {
                      const newIngredients = [...ingredients];
                      newIngredients[index].quantity = e.target.value;
                      setIngredients(newIngredients);
                    }}
                    aria-label={`Quantity for ingredient ${index + 1}`}
                  />
                </SmallInputContainer>

                <Select
                  value={ingredient.measure}
                  onChange={(e) => {
                    const newIngredients = [...ingredients];
                    newIngredients[index].measure = e.target.value;
                    setIngredients(newIngredients);
                  }}
                >
                  <option value="">Select Measure</option>
                  <option value="g">g</option>
                  <option value="kg">kg</option>
                  <option value="ml">ml</option>
                  <option value="cup">Cup</option>
                  <option value="tbsp">Tbsp</option>
                  <option value="tsp">Tsp</option>
                </Select>

                <SmallInputContainer>
                  <SmallInput
                    type="text"
                    placeholder="Ingredient"
                    value={ingredient.name}
                    onChange={(e) => {
                      const newIngredients = [...ingredients];
                      newIngredients[index].name = e.target.value;
                      setIngredients(newIngredients);
                    }}
                    aria-label={`Ingredient name for ingredient ${index + 1}`}
                  />
                </SmallInputContainer>
              </Row>
            </IngredientGroup>
          ))}
          <AddButton
            type="button"
            onClick={() => setIngredients([...ingredients, { quantity: "", measure: "", name: "", substitute: "", showSubstitute: false }])}
          >
            + Add Another Ingredient
          </AddButton>
        </FormGroup>

        {/* Instructions */}
        <FormGroup>
          <Label>Instructions</Label>
          {instructions.map((step, index) => (
            <div key={index} style={{ display: 'flex', flexDirection: 'column', position: 'relative', gap: '5px' }}>
              <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
                <FullWidthTextarea
                  placeholder={`Step ${index + 1}`}
                  value={step}
                  onChange={(e) => {
                    const newInstructions = [...instructions];
                    newInstructions[index] = e.target.value;
                    setInstructions(newInstructions);
                  }}
                  style={{ flex: 1 }}
                />
              </div>
            </div>
          ))}
          <AddButton
            type="button"
            onClick={() => setInstructions([...instructions, ""])}
          >
            + Add Step
          </AddButton>
        </FormGroup>

        {/* Labels Section */}
        <FormGroup>
          <Label>Labels (Select any that apply to your recipe):</Label>
          <TagContainer>
            {tags
              .filter((tag) => !selectedTags.find((t) => t.label === tag.label))
              .map((tag, index) => (
                <Tag key={index} onClick={() => handleTagClick(tag)}>
                  {tag.icon} {tag.label}
                </Tag>
              ))}
          </TagContainer>
          <SelectedTagsContainer>
            {selectedTags.map((tag, index) => (
              <SelectedTag key={index} onClick={() => handleRemoveTag(tag)}>
                {tag.icon} {tag.label}
              </SelectedTag>
            ))}
          </SelectedTagsContainer>
        </FormGroup>

        {/* Categories Section */}
        <FormGroup>
          <Label>Categories (Choose relevant categories for your recipe):</Label>
          <CategoryContainer>
            {categories.map((category, index) => (
              <CategoryOption
                key={index}
                onClick={() => handleCategoryClick(category)}
                className={selectedCategories.includes(category) ? "selected" : ""}
              >
                {category}
              </CategoryOption>
            ))}
          </CategoryContainer>
        </FormGroup>

        {/* Submit Button */}
        <SubmitButton type="submit">
          Submit Recipe
        </SubmitButton>
      </Form>
    </PageWrapper>
  );
};

export default AddRecipePage;
