import React from 'react';
import { Link } from 'react-router-dom';
import './css/Navbar.css';
import logo from './logo.png';  // Import your logo image

function Navbar({ isLoggedIn, setIsLoggedIn }) {
  return (
    <nav className="navbar">
      {/* Left section: Logo */}
      <div className="navbar-logo">
        <Link to="/">
          <img src={logo} alt="Culinareat Logo" className="logo" />
        </Link>
      </div>

      {/* Right section: Links for logged-in and non-logged-in users */}
      <div className="navbar-right">
        {isLoggedIn ? (
          <>
            {/* Add Recipe button in the header for logged-in users */}
            <Link to="/add-recipe" className="navbar-button add-recipe-btn">Add Recipe</Link>
            <button className="logout-button" onClick={() => setIsLoggedIn(false)}>Logout</button>
          </>
        ) : (
          <>
            <Link to="/register" className="navbar-button">Register</Link>
            <Link to="/login" className="navbar-button">Login</Link>
          </>
        )}
      </div>

      {/* Bottom navbar (if needed for other links) */}
      {isLoggedIn && (
        <div className="bottom-navbar">
          <ul className="navbar-links">
            <li><Link to="/">Home</Link></li>
            <li><Link to="/add-recipe">Add Recipe</Link></li>
            <li><Link to="/shopping-list">Shopping List</Link></li>
            <li><Link to="/browse-recipes">Browse Recipes</Link></li>
          </ul>
          <div className="search-bar">
            <input type="text" placeholder="Search..." />
            <button type="submit">
              <i className="fas fa-search"></i>
            </button>
          </div>
        </div>
      )}
    </nav>
  );
}

export default Navbar;
