import React, { useState } from 'react';
import styled from 'styled-components';
import { FiInfo } from 'react-icons/fi';

const PageWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 40px;
  min-height: 100vh;
  background-color: #f5f5f7;
  font-family: 'Raleway', sans-serif;
`;

const Form = styled.form`
  width: 100%;
  max-width: 600px;
  background-color: #fff;
  padding: 30px;
  border-radius: 10px;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.1);
`;

const FormGroup = styled.div`
  margin-bottom: 20px;
  display: flex;
  flex-direction: column;
`;

const Label = styled.label`
  font-size: 16px;
  color: #333;
  font-family: 'Poppins', sans-serif;
  font-weight: 600;
  display: flex;
  align-items: center;
  margin-bottom: 5px;
`;

const InfoIcon = styled(FiInfo)`
  margin-left: 5px;
  color: #888;
  cursor: pointer;
`;

const Input = styled.input`
  padding: 12px 15px;
  border-radius: 5px;
  border: 1px solid #ddd;
  font-size: 16px;
  width: 100%;
  background-color: #f9f9f9;
  transition: border-color 0.3s;
  &:focus {
    border-color: #5b9e5d;
    outline: none;
  }
`;

const FullWidthTextarea = styled.textarea`
  width: 100%;
  padding: 12px 15px;
  border-radius: 5px;
  border: 1px solid #ddd;
  font-size: 16px;
  background-color: #f9f9f9;
  resize: vertical;
  transition: border-color 0.3s;
  &:focus {
    border-color: #5b9e5d;
    outline: none;
  }
`;

const IngredientGroup = styled.div`
  display: flex;
  gap: 10px;
  align-items: center;
  margin-bottom: 10px;
`;

const Select = styled.select`
  padding: 12px 15px;
  border-radius: 5px;
  border: 1px solid #ddd;
  font-size: 16px;
  width: 100%;
  background-color: #f9f9f9;
  transition: border-color 0.3s;
  &:focus {
    border-color: #5b9e5d;
    outline: none;
  }
`;

const Button = styled.button`
  border-radius: 5px;
  padding: 12px 20px;
  font-size: 16px;
  font-family: 'Poppins', sans-serif;
  cursor: pointer;
  color: #FFFFFF;
  background-color: #5b9e5d;
  border: none;
  transition: background-color 0.3s, transform 0.2s;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1);
  margin-top: 10px;
  width: 100%;

  &:hover {
    background-color: #4a8c50;
    transform: translateY(-2px);
  }
`;

const SubmitButton = styled(Button)`
  background-color: #4a8c50;
`;

const Title = styled.h2`
  text-align: center;
  margin-bottom: 30px;
  font-family: 'EB Garamond', serif;
  color: #333;
  font-size: 24px;
  font-weight: bold;
`;

const AddRecipePage = () => {
  const [recipeName, setRecipeName] = useState("");
  const [description, setDescription] = useState("");
  const [ingredients, setIngredients] = useState([{ quantity: "", measure: "", name: "", substitute: "" }]);
  const [instructions, setInstructions] = useState([""]);
  const [showSubstitute, setShowSubstitute] = useState(false);

  const handleAddIngredient = () => setIngredients([...ingredients, { quantity: "", measure: "", name: "", substitute: "" }]);
  const handleDeleteIngredient = (index) => setIngredients(ingredients.filter((_, i) => i !== index));

  const handleAddStep = () => setInstructions([...instructions, ""]);
  const handleDeleteStep = (index) => setInstructions(instructions.filter((_, i) => i !== index));

  const handleFormSubmit = (e) => {
    e.preventDefault();
    const recipeData = { recipeName, description, ingredients, instructions };
    console.log("Recipe Data:", recipeData);
  };

  return (
    <PageWrapper>
      <Title>Add a New Recipe</Title>
      <Form onSubmit={handleFormSubmit}>
        <FormGroup>
          <Label htmlFor="recipeName">What would you like to name your recipe? <InfoIcon /></Label>
          <Input
            type="text"
            id="recipeName"
            placeholder="E.g., Grandma's Apple Pie"
            value={recipeName}
            onChange={(e) => setRecipeName(e.target.value)}
          />
        </FormGroup>

        <FormGroup>
          <Label htmlFor="description">Describe your recipe:</Label>
          <FullWidthTextarea
            id="description"
            placeholder="Add a short description of the recipe"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
        </FormGroup>

        <FormGroup>
          <Label>What ingredients will you need?</Label>
          {ingredients.map((ingredient, index) => (
            <IngredientGroup key={index}>
              <Input
                type="text"
                placeholder="Quantity"
                value={ingredient.quantity}
                onChange={(e) => {
                  const newIngredients = [...ingredients];
                  newIngredients[index].quantity = e.target.value;
                  setIngredients(newIngredients);
                }}
              />
              <Select
                value={ingredient.measure}
                onChange={(e) => {
                  const newIngredients = [...ingredients];
                  newIngredients[index].measure = e.target.value;
                  setIngredients(newIngredients);
                }}
              >
                <option value="">Select Measure</option>
                <option value="g">g</option>
                <option value="kg">kg</option>
                <option value="ml">ml</option>
                <option value="cup">Cup</option>
                <option value="tbsp">Tbsp</option>
                <option value="tsp">Tsp</option>
              </Select>
              <Input
                type="text"
                placeholder="Ingredient"
                value={ingredient.name}
                onChange={(e) => {
                  const newIngredients = [...ingredients];
                  newIngredients[index].name = e.target.value;
                  setIngredients(newIngredients);
                }}
              />
              <Button type="button" onClick={() => handleDeleteIngredient(index)} style={{ backgroundColor: '#dc3545' }}>
                - Delete
              </Button>
            </IngredientGroup>
          ))}
          <Button type="button" onClick={handleAddIngredient}>+ Add Another Ingredient</Button>
        </FormGroup>

        {showSubstitute && (
          <FormGroup>
            <Label>Substitute Ingredient:</Label>
            <Input
              type="text"
              placeholder="Substitute Ingredient"
              onChange={(e) => {
                const newIngredients = [...ingredients];
                newIngredients[newIngredients.length - 1].substitute = e.target.value;
                setIngredients(newIngredients);
              }}
            />
          </FormGroup>
        )}

        <FormGroup>
          <Label>Instructions:</Label>
          {instructions.map((step, index) => (
            <FormGroup key={index}>
              <FullWidthTextarea
                placeholder={`Step ${index + 1}`}
                value={step}
                onChange={(e) => {
                  const newInstructions = [...instructions];
                  newInstructions[index] = e.target.value;
                  setInstructions(newInstructions);
                }}
              />
              <Button type="button" onClick={() => handleDeleteStep(index)} style={{ backgroundColor: '#dc3545', marginTop: '5px' }}>
                - Delete Step
              </Button>
            </FormGroup>
          ))}
          <Button type="button" onClick={handleAddStep}>+ Add Step</Button>
        </FormGroup>

        <FormGroup>
          <Label htmlFor="photo">Photo:</Label>
          <Input type="file" id="photo" />
        </FormGroup>

        <SubmitButton type="submit">Submit Recipe</SubmitButton>
      </Form>
    </PageWrapper>
  );
};

export default AddRecipePage;
