import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import axios from 'axios';
/*import loginImage from '../images/login-image.png';*/ // Replace with your actual image path

const PageWrapper = styled.div`
  display: flex;
  flex-direction: row-reverse;
  height: 100vh;
  width: 100vw;
  background: linear-gradient(135deg, #e9e4e0 0%, #f3ece8 100%);
`;

const RightImageContainer = styled.div`
  flex: 1;
  position: relative;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const OverlayText = styled.div`
  position: absolute;
  top: 20%;
  color: #ffffff;
  font-size: 2rem;
  text-align: center;
  padding: 30px;
  background-color: rgba(0, 0, 0, 0.5);
  border-radius: 10px;
  font-family: 'Dancing Script', cursive;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.7);
  letter-spacing: 1px;
  line-height: 1.5;
  text-shadow: 2px 2px 8px rgba(0, 0, 0, 0.8);
`;

const LoginImage = styled.img`
  width: 300px;
  height: auto;
  margin-top: 20px;
  border-radius: 15px;
  box-shadow: 0px 8px 16px rgba(0, 0, 0, 0.1);
`;

const LeftFormContainer = styled.div`
  flex: 1;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const FormContainer = styled.form`
  width: 100%;
  max-width: 400px;
  text-align: center;
  padding: 30px;
  border-radius: 15px;
  background: linear-gradient(135deg, #ffffff 0%, #f5f5f5 100%);
  box-shadow: 0px 8px 24px rgba(0, 0, 0, 0.2);
`;

const FormHeading = styled.h2`
  font-size: 28px;
  color: #333;
  margin-bottom: 20px;
  font-family: 'EB Garamond', serif;
`;

const FormGroup = styled.div`
  margin-bottom: 20px;
  position: relative;
  display: flex;
  flex-direction: column;
`;

const Label = styled.label`
  text-align: left;
  margin-bottom: 5px;
  font-size: 14px;
  color: #777;
`;

const Input = styled.input`
  width: 100%;
  padding: 12px;
  border-radius: 5px;
  border: 1px solid ${({ isError }) => (isError ? '#e74c3c' : '#ccc')};
  font-size: 16px;
  box-sizing: border-box;
  transition: border-color 0.3s, box-shadow 0.3s;

  &:focus {
    border-color: ${({ isError }) => (isError ? '#e74c3c' : '#5b9e5d')};
    box-shadow: 0 0 8px rgba(92, 158, 93, 0.3);
    outline: none;
  }
`;

const ErrorTextInsideInput = styled.span`
  position: absolute;
  bottom: -5px;
  left: 10px;
  color: #e74c3c;
  font-size: 12px;
  background: #fff;
  padding: 0 5px;
  transform: translateY(100%);
  white-space: nowrap;
`;

const ForgotPasswordLink = styled.a`
  margin-top: 5px;
  font-size: 14px;
  color: #8b5e3c;
  text-decoration: none;
  cursor: pointer;
  align-self: flex-start; /* Align the link to the left */
  transition: color 0.3s ease;

  &:hover {
    color: #70462c;
  }
`;

const SubmitButton = styled.button`
  width: 100%;
  padding: 12px;
  background-color: #8b5e3c;
  color: white;
  border: none;
  border-radius: 12px;
  cursor: pointer;
  font-size: 16px;
  margin-top: 20px;
  transition: background-color 0.3s, transform 0.2s;

  &:hover {
    background-color: #70462c;
    transform: translateY(-3px);
  }
`;

function LoginPage({ setIsLoggedIn }) {
  const [formData, setFormData] = useState({
    UserName: '',
    Password: '',
  });
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const validateUsername = (value) => {
    if (!value) return 'Username is required.';
    return '';
  };

  const validatePassword = (value) => {
    if (!value) {
      return 'Password is required.';
    }
    if (!/(?=.*[A-Za-z])(?=.*\d)[A-Za-z\d]{6,}/.test(value)) {
      return 'Password must be at least 6 characters, including one letter and one number.';
    }
    return '';
  };

  const handleChange = (e) => {
    const { name, value } = e.target;

    // Update form data
    setFormData((prevData) => ({ ...prevData, [name]: value }));

    // Dynamically validate inputs
    if (name === 'UserName') {
      setErrors((prevErrors) => ({
        ...prevErrors,
        UserName: validateUsername(value),
      }));
    }

    if (name === 'Password') {
      setErrors((prevErrors) => ({
        ...prevErrors,
        Password: validatePassword(value),
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Final validation before submission
    const usernameError = validateUsername(formData.UserName);
    const passwordError = validatePassword(formData.Password);

    if (usernameError || passwordError) {
      setErrors({
        UserName: usernameError,
        Password: passwordError,
      });
      return;
    }

    try {
      // Submit login request
      await axios.post('http://localhost:5001/login', formData, {
        headers: {
          'Content-Type': 'application/json',
        },
        withCredentials: true,
      });

      setIsLoggedIn(true);
      localStorage.setItem('isLoggedIn', 'true');
      localStorage.setItem('userName', formData.UserName);
      navigate('/');
    } catch (error) {
      setErrors((prevErrors) => ({
        ...prevErrors,
        api: 'Invalid username or password.',
      }));
    }
  };

  return (
    <PageWrapper>
      <LeftFormContainer>
        <FormContainer onSubmit={handleSubmit}>
          <FormHeading>Login to CulinarEAT</FormHeading>
          <FormGroup>
            <Label htmlFor="UserName">Username</Label>
            <Input
              type="text"
              id="UserName"
              name="UserName"
              value={formData.UserName}
              onChange={handleChange}
              isError={!!errors.UserName}
            />
            {errors.UserName && (
              <ErrorTextInsideInput>{errors.UserName}</ErrorTextInsideInput>
            )}
          </FormGroup>
          <FormGroup>
            <Label htmlFor="Password">Password</Label>
            <Input
              type="password"
              id="Password"
              name="Password"
              value={formData.Password}
              onChange={handleChange}
              isError={!!errors.Password}
            />
            {errors.Password && (
              <ErrorTextInsideInput>{errors.Password}</ErrorTextInsideInput>
            )}
            <ForgotPasswordLink href="/forgot-password">
              Forgot Password?
            </ForgotPasswordLink>
          </FormGroup>
          <SubmitButton type="submit">Login</SubmitButton>
          {errors.api && <ErrorTextInsideInput>{errors.api}</ErrorTextInsideInput>}
        </FormContainer>
      </LeftFormContainer>
      <RightImageContainer>
        <OverlayText>
          Glad to see you back! <br />
          Login to continue your culinary journey.
        
        </OverlayText>
      </RightImageContainer>
    </PageWrapper>
  );
}

export default LoginPage;
