import React, { useState } from 'react';
import styled from 'styled-components';

// Styled Components
const PageWrapper = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    padding: 40px;
    min-height: 100vh;
    background-color: #D6C0B3;
    font-family: 'Raleway', sans-serif;
`;

const Form = styled.form`
    width: 100%;
    max-width: 600px;
    box-sizing: border-box;
`;

const FormGroup = styled.div`
    margin-bottom: 20px;
    display: flex;
    flex-direction: column;
    position: relative;
`;

const PhotoFormGroup = styled(FormGroup)`
    margin-top: 20px;
`;

const Label = styled.label`
    margin-bottom: 8px;
    font-size: 16px;
    color: #6f6666;
    font-family: 'Poppins', sans-serif;
    font-weight: bold;
`;

const FullWidthTextarea = styled.textarea`
    width: 100%;
    padding: 12px;
    border-radius: 5px;
    border: 1px solid #ccc;
    font-size: 16px;
    box-sizing: border-box;
    resize: vertical;
    transition: border-color 0.3s;
    &:focus {
        border-color: #c5705d;
        outline: none;
    }
`;

const IngredientGroup = styled.div`
    display: flex;
    flex-direction: column;
    gap: 10px;
    margin-bottom: 20px;
    position: relative;
`;

const IngredientInputs = styled.div`
    display: flex;
    gap: 10px;
`;

const Input = styled.input`
    padding: 12px;
    border-radius: 5px;
    border: 1px solid ${props => (props.error ? '#d9534f' : '#ccc')};
    font-size: 16px;
    width: 100%;
    transition: border-color 0.3s;
    &:focus {
        border-color: ${props => (props.error ? '#d9534f' : '#c5705d')};
        outline: none;
    }
`;

const Select = styled.select`
    padding: 12px;
    border-radius: 5px;
    border: 1px solid #ccc;
    font-size: 16px;
    width: 100%;
    transition: border-color 0.3s;
    &:focus {
        border-color: #c5705d;
        outline: none;
    }
`;

const ButtonGroup = styled.div`
    display: flex;
    gap: 10px;
    margin-top: 10px;
`;

const Button = styled.button`
    border-radius: 5px;
    padding: 10px 15px;
    font-size: 14px;
    font-family: 'Poppins', sans-serif;
    cursor: pointer;
    text-align: center;
    color: #FFFFFF;
    transition: background-color 0.3s;
    border: none;

    &.add {
        background-color: #5b9e5d;
    }

    &.add:hover {
        background-color: #3d493e;
    }

    &.delete {
        background-color: #de3a3ac8;
    }

    &.delete:hover {
        background-color: #7b5852;
    }

    &.substitute {
        background-color: #f0ad4e;
    }

    &.substitute:hover {
        background-color: #c08e2b;
    }
`;

const SubmitButton = styled.button`
    width: 100%;
    padding: 15px;
    background-color: #c5705d;
    color: #FFFFFF;
    border: none;
    border-radius: 8px;
    cursor: pointer;
    font-size: 16px;
    font-family: 'Poppins', sans-serif;
    font-weight: bold;
    transition: background-color 0.3s;
    text-align: center;

    &:hover {
        background-color: #ab886d;
    }
`;

const Message = styled.div`
    margin-top: 20px;
    padding: 12px;
    border-radius: 5px;
    font-size: 14px;
    width: 100%;
    text-align: center;
    font-family: 'Poppins', sans-serif;
    color: ${props => (props.type === 'error' ? '#d9534f' : '#5b9e5d')};
    background-color: ${props => (props.type === 'error' ? '#f8d7da' : '#dff0d8')};
`;

const Title = styled.h2`
    text-align: center;
    margin-bottom: 30px;
    font-family: 'EB Garamond', serif;
    color: #d77a65;
    font-size: 32px;
    font-weight: bold;
`;

const Subtitle = styled.h3`
    font-family: 'Poppins', sans-serif;
    color: #6f6666;
    font-size: 18px;
    font-weight: bold;
    margin-top: 20px;
    margin-bottom: 10px;
`;

const AddRecipePage = () => {
    const [recipeName, setRecipeName] = useState('');
    const [description, setDescription] = useState('');
    const [instructions, setInstructions] = useState(['']);
    const [ingredients, setIngredients] = useState([{ quantity: '', measure: '', ingredient: '', substitute: '' }]);
    const [photo, setPhoto] = useState(null);
    const [formErrors, setFormErrors] = useState({});
    const [successMessage, setSuccessMessage] = useState('');

    const handleIngredientChange = (index, event) => {
        const { name, value } = event.target;
        const updatedIngredients = [...ingredients];
        updatedIngredients[index][name] = value;
        setIngredients(updatedIngredients);
    };

    const handleAddIngredient = () => {
        setIngredients([...ingredients, { quantity: '', measure: '', ingredient: '', substitute: '' }]);
    };

    const handleDeleteIngredient = (index) => {
        const updatedIngredients = ingredients.filter((_, i) => i !== index);
        setIngredients(updatedIngredients);
    };

    const handleAddSubstitute = (index) => {
        const updatedIngredients = [...ingredients];
        updatedIngredients[index].substitute = ''; // Initialize substitute field
        setIngredients(updatedIngredients);
    };

    const handleInstructionChange = (index, event) => {
        const newInstructions = [...instructions];
        newInstructions[index] = event.target.value;
        setInstructions(newInstructions);
    };

    const handleAddInstruction = () => {
        setInstructions([...instructions, '']);
    };

    const handleDeleteInstruction = (index) => {
        const updatedInstructions = instructions.filter((_, i) => i !== index);
        setInstructions(updatedInstructions);
    };

    const handlePhotoChange = (e) => {
        setPhoto(e.target.files[0]);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        setSuccessMessage('Recipe added successfully!');
        console.log('Recipe submitted:', {
            recipeName,
            description,
            ingredients,
            instructions,
            photo,
        });
    };

    return (
        <PageWrapper>
            <Title>Add a New Recipe</Title>
            <Form onSubmit={handleSubmit}>
                <FormGroup>
                    <Label htmlFor="recipeName">Recipe Name:</Label>
                    <Input
                        type="text"
                        id="recipeName"
                        name="recipeName"
                        value={recipeName}
                        onChange={(e) => setRecipeName(e.target.value)}
                    />
                </FormGroup>

                <FormGroup>
                    <Label htmlFor="description">Description:</Label>
                    <FullWidthTextarea
                        id="description"
                        value={description}
                        onChange={(e) => setDescription(e.target.value)}
                        placeholder="Add a short description of the recipe"
                    />
                </FormGroup>

                <Subtitle>Ingredients</Subtitle>
                {ingredients.map((ingredient, index) => (
                    <IngredientGroup key={index}>
                        <IngredientInputs>
                            <Input
                                type="text"
                                name="quantity"
                                placeholder="Quantity"
                                value={ingredient.quantity}
                                onChange={(e) => handleIngredientChange(index, e)}
                            />
                            <Select
                                name="measure"
                                value={ingredient.measure}
                                onChange={(e) => handleIngredientChange(index, e)}
                            >
                                <option>Select Measure</option>
                                {/* Add more options here */}
                            </Select>
                            <Input
                                type="text"
                                name="ingredient"
                                placeholder="Ingredient"
                                value={ingredient.ingredient}
                                onChange={(e) => handleIngredientChange(index, e)}
                            />
                        </IngredientInputs>
                        {ingredient.substitute !== undefined && (
                            <Input
                                type="text"
                                name="substitute"
                                placeholder="Substitute Ingredient"
                                value={ingredient.substitute}
                                onChange={(e) => handleIngredientChange(index, e)}
                                style={{ marginTop: '10px' }}
                            />
                        )}
                        <ButtonGroup>
                            <Button type="button" className="add" onClick={handleAddIngredient}>+ Add Ingredient</Button>
                            <Button type="button" className="delete" onClick={() => handleDeleteIngredient(index)}>- Delete</Button>
                            <Button type="button" className="substitute" onClick={() => handleAddSubstitute(index)}>+ Substitute</Button>
                        </ButtonGroup>
                    </IngredientGroup>
                ))}

                <Subtitle>Instructions</Subtitle>
                {instructions.map((instruction, index) => (
                    <FormGroup key={index}>
                        <FullWidthTextarea
                            placeholder={`Step ${index + 1}`}
                            value={instruction}
                            onChange={(e) => handleInstructionChange(index, e)}
                        />
                    </FormGroup>
                ))}
                <ButtonGroup>
                    <Button type="button" className="add" onClick={handleAddInstruction}>+ Add Step</Button>
                    <Button type="button" className="delete" onClick={() => handleDeleteInstruction(instructions.length - 1)}>- Delete</Button>
                </ButtonGroup>

                <PhotoFormGroup>
                    <Label htmlFor="photo">Photo:</Label>
                    <Input type="file" id="photo" name="photo" onChange={handlePhotoChange} />
                </PhotoFormGroup>

                <SubmitButton type="submit">Submit Recipe</SubmitButton>
                
                {successMessage && (
                    <Message type="success">{successMessage}</Message>
                )}
            </Form>
        </PageWrapper>
    );
};

export default AddRecipePage;
