import React from 'react';
import breakfastImage from './images/breakfast.jpg'; // Breakfast Image

const styles = {
  page: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    backgroundColor: '#f9f7f4',
    width: '100%',
    minHeight: '100vh',
  },
  heroSection: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    height: '30vh',
    width: '100%',
    backgroundImage: `url(${breakfastImage})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    color: '#8B4513', // Text color
    textAlign: 'center',
    position: 'relative',
  },
  heroText: {
    fontSize: '100px',
    fontWeight: 'bold',
    color: '#fff', // White text color
    textShadow: '2px 2px 0 #8B4513, -2px -2px 0 #8B4513, 2px -2px 0 #8B4513, -2px 2px 0 #8B4513', // Brown frame
    padding: '10px',
  },
  contentSection: {
    padding: '20px',
    textAlign: 'center',
    fontFamily: 'Georgia, serif',
    color: '#333',
  },
  headline: {
    fontSize: '32px',
    fontWeight: 'bold',
    margin: '20px 0',
    color: '#8B4513',
  },
  description: {
    fontSize: '22px', 
    lineHeight: '1.8', 
  },
  footer: {
    marginTop: 'auto',
    width: '100%',
    padding: '10px',
    color: '#333', // Footer text color
    textAlign: 'center',
    fontFamily: 'Georgia, serif',
    backgroundColor: 'transparent', // Removed yellow background
  },
};

function BreakfastPage() {
  return (
    <div style={styles.page}>
      {/* Hero Section */}
      <div style={styles.heroSection}>
        <h1 style={styles.heroText}>Breakfast Recipes</h1>
      </div>

      {/* Content Section */}
      <div style={styles.contentSection}>
        <h2 style={styles.headline}>Start Your Day Right!</h2>
        <p style={styles.description}>
          <span style={{ display: 'block' }}>
            Explore our delightful breakfast recipes, perfect for energizing your mornings.
          </span>
          <span style={{ display: 'block' }}>
            From quick meals to indulgent treats, we have something for everyone!
          </span>
        </p>
      </div>
    </div>
  );
}

export default BreakfastPage;
