import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import axios from 'axios';
import pic3 from './images/img1.png'; // Import the image

const PageWrapper = styled.div`
  display: flex;
  flex-direction: row;
  height: 100vh;
  width: 100vw;
  background: linear-gradient(135deg, #e9e4e0 0%, #f3ece8 100%);
`;

const RightFormContainer = styled.div`
  flex: 1;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const FormContainer = styled.form`
  width: 100%;
  max-width: 400px;
  text-align: center;
  padding: 30px;
  border-radius: 15px;
  background: linear-gradient(135deg, #ffffff 0%, #f5f5f5 100%);
  box-shadow: 0px 8px 24px rgba(0, 0, 0, 0.2);
`;

const ModernHeadline = styled.h1`
  font-size: 2.5rem;
  color: #333;
  font-family: 'Montserrat', sans-serif;
  margin-bottom: 10px;
  font-weight: 700;
`;

const SubHeadline = styled.h2`
  font-size: 1.2rem;
  color: #666;
  font-family: 'Raleway', sans-serif;
  margin-bottom: 20px;
`;

const FormGroup = styled.div`
  margin-bottom: 20px;
  position: relative;
`;

const Label = styled.label`
  text-align: left;
  margin-bottom: 5px;
  font-size: 14px;
  color: #777;
`;

const InputWrapper = styled.div`
  position: relative;
  display: flex;
  align-items: center;
`;

const Input = styled.input`
  width: 100%;
  padding: 12px 40px 12px 12px;
  border-radius: 5px;
  border: 1px solid ${({ isError }) => (isError ? '#e74c3c' : '#ccc')};
  font-size: 16px;
  box-sizing: border-box;
  transition: border-color 0.3s, box-shadow 0.3s;

  &:focus {
    border-color: ${({ isError }) => (isError ? '#e74c3c' : '#5b9e5d')};
    box-shadow: 0 0 8px rgba(92, 158, 93, 0.3);
    outline: none;
  }
`;

const CheckmarkIcon = styled.span`
  position: absolute;
  right: 10px;
  font-size: 16px;
  color: ${({ isValid }) => (isValid ? '#5b9e5d' : 'transparent')};
  transition: color 0.3s;
`;

const ErrorText = styled.div`
  color: #e74c3c;
  font-size: 14px;
  height: 18px;
  visibility: ${({ isVisible }) => (isVisible ? 'visible' : 'hidden')};
  margin-top: 5px;
`;

const SubmitButton = styled.button`
  width: 100%;
  padding: 12px;
  background-color: #8b5e3c;
  color: white;
  border: none;
  border-radius: 12px;
  cursor: pointer;
  font-size: 16px;
  margin-top: 20px;
  transition: background-color 0.3s, transform 0.2s;

  &:hover {
    background-color: #70462c;
    transform: translateY(-3px);
  }
`;

const LeftImageContainer = styled.div`
  flex: 1;
  position: relative;
  display: flex;
  justify-content: center;
  align-items: center;
  overflow: hidden;
`;

const StyledImage = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
  position: absolute;
  z-index: -1; /* Ensures the image is behind the text */
`;

const OverlayText = styled.div`
  position: relative;
  color: #ffffff;
  font-size: 2rem;
  text-align: center;
  padding: 30px;
  background-color: rgba(0, 0, 0, 0.5);
  border-radius: 10px;
  font-family: 'Dancing Script', cursive;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.7);
  letter-spacing: 1px;
  line-height: 1.5;
  text-shadow: 2px 2px 8px rgba(0, 0, 0, 0.8);
  z-index: 1;
`;

function LoginPage({ setIsLoggedIn }) {
  const [formData, setFormData] = useState({
    UserName: '',
    Password: '',
  });
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const validateUsername = (value) => (!value ? 'Username is required.' : '');
  const validatePassword = (value) =>
    !value ? 'Password is required.' : value.length < 6 ? 'Password must be at least 6 characters.' : '';

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));

    if (name === 'UserName') setErrors((prev) => ({ ...prev, UserName: validateUsername(value) }));
    if (name === 'Password') setErrors((prev) => ({ ...prev, Password: validatePassword(value) }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const usernameError = validateUsername(formData.UserName);
    const passwordError = validatePassword(formData.Password);

    if (usernameError || passwordError) {
      setErrors({ UserName: usernameError, Password: passwordError });
      return;
    }

    try {
      await axios.post('http://localhost:5001/login', formData, { headers: { 'Content-Type': 'application/json' } });
      setIsLoggedIn(true);
      localStorage.setItem('isLoggedIn', 'true');
      navigate('/');
    } catch {
      setErrors((prev) => ({ ...prev, api: 'Invalid username or password.' }));
    }
  };

  console.log('Debug: Image Path ->', pic3); // Debug the image path

  return (
    <PageWrapper>
      <LeftImageContainer>
        <StyledImage src={pic3} alt="Culinary Background" />
        <OverlayText>
          Glad to see you back! <br />
          Login to continue your culinary journey.
        </OverlayText>
      </LeftImageContainer>
      <RightFormContainer>
        <FormContainer onSubmit={handleSubmit}>
          <ModernHeadline>CulinarEAT</ModernHeadline>
          <SubHeadline>Welcome Back!</SubHeadline>
          <FormGroup>
            <Label htmlFor="UserName">Username</Label>
            <InputWrapper>
              <Input
                type="text"
                id="UserName"
                name="UserName"
                value={formData.UserName}
                onChange={handleChange}
                isError={!!errors.UserName}
              />
              <CheckmarkIcon isValid={!errors.UserName && formData.UserName}>✔</CheckmarkIcon>
            </InputWrapper>
            <ErrorText isVisible={!!errors.UserName}>{errors.UserName}</ErrorText>
          </FormGroup>
          <FormGroup>
            <Label htmlFor="Password">Password</Label>
            <InputWrapper>
              <Input
                type="password"
                id="Password"
                name="Password"
                value={formData.Password}
                onChange={handleChange}
                isError={!!errors.Password}
              />
              <CheckmarkIcon isValid={!errors.Password && formData.Password}>✔</CheckmarkIcon>
            </InputWrapper>
            <ErrorText isVisible={!!errors.Password}>{errors.Password}</ErrorText>
          </FormGroup>
          <SubmitButton type="submit">Sign In</SubmitButton>
          {errors.api && <ErrorText isVisible>{errors.api}</ErrorText>}
        </FormContainer>
      </RightFormContainer>
    </PageWrapper>
  );
}

export default LoginPage;
