import React, { useState } from 'react';
import styled from 'styled-components';

// Styled Components
const PageWrapper = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    padding: 40px;
    min-height: 100vh;
    background-color: #D6C0B3;
    font-family: 'Raleway', sans-serif;
`;

const Form = styled.form`
    width: 100%;
    max-width: 600px;
    box-sizing: border-box;
`;

const FormGroup = styled.div`
    margin-bottom: 20px;
    display: flex;
    flex-direction: column;
    position: relative;
`;

const PhotoFormGroup = styled(FormGroup)`
    margin-top: 20px;
`;

const Label = styled.label`
    margin-bottom: 8px;
    font-size: 16px;
    color: #6f6666;
    font-family: 'Poppins', sans-serif;
    font-weight: bold;
`;

const FullWidthTextarea = styled.textarea`
    width: 100%;
    padding: 12px;
    border-radius: 5px;
    border: 1px solid #ccc;
    font-size: 16px;
    box-sizing: border-box;
    resize: vertical;
    transition: border-color 0.3s;
    &:focus {
        border-color: #c5705d;
        outline: none;
    }
`;

const IngredientGroup = styled.div`
    display: flex;
    gap: 10px;
    align-items: center;
    margin-bottom: 20px;
    position: relative;
`;

const Input = styled.input`
    padding: 12px;
    border-radius: 5px;
    border: 1px solid ${props => (props.error ? '#d9534f' : '#ccc')};
    font-size: 16px;
    width: 100%;
    transition: border-color 0.3s;
    &:focus {
        border-color: ${props => (props.error ? '#d9534f' : '#c5705d')};
        outline: none;
    }
`;

const Select = styled.select`
    padding: 12px;
    border-radius: 5px;
    border: 1px solid #ccc;
    font-size: 16px;
    width: 100%;
    transition: border-color 0.3s;
    &:focus {
        border-color: #c5705d;
        outline: none;
    }
`;

const ErrorText = styled.span`
    color: #d9534f;
    font-size: 14px;
    position: absolute;
    top: 100%;
    left: 0;
    margin-top: 3px;
    display: ${props => (props.show ? 'block' : 'none')};
`;

const ButtonGroup = styled.div`
    display: flex;
    gap: 10px;
    margin-top: 10px;
`;

const Button = styled.button`
    border-radius: 5px;
    padding: 10px 15px;
    font-size: 14px;
    font-family: 'Poppins', sans-serif;
    cursor: pointer;
    color: #FFFFFF;
    transition: background-color 0.3s;
    border: none;
    display: flex;
    align-items: center;

    &.add {
        background-color: #5b9e5d;
    }

    &.add:hover {
        background-color: #3d493e;
    }

    &.delete {
        background-color: #de3a3ac8;
    }

    &.delete:hover {
        background-color: #7b5852;
    }

    &.substitute {
        background-color: #e6a23c;
    }

    &.substitute:hover {
        background-color: #cf8a23;
    }
`;

const SubmitButton = styled.button`
    width: 100%;
    padding: 15px;
    background-color: #c5705d;
    color: #FFFFFF;
    border: none;
    border-radius: 8px;
    cursor: pointer;
    font-size: 16px;
    font-family: 'Poppins', sans-serif;
    font-weight: bold;
    transition: background-color 0.3s;
    text-align: center;

    &:hover {
        background-color: #ab886d;
    }
`;

const Message = styled.div`
    margin-top: 20px;
    padding: 12px;
    border-radius: 5px;
    font-size: 14px;
    width: 100%;
    text-align: center;
    font-family: 'Poppins', sans-serif;
    color: ${props => (props.type === 'error' ? '#d9534f' : '#5b9e5d')};
    background-color: ${props => (props.type === 'error' ? '#f8d7da' : '#dff0d8')};
`;

const Title = styled.h2`
    text-align: center;
    margin-bottom: 30px;
    font-family: 'EB Garamond', serif;
    color: #d77a65;
    font-size: 32px;
    font-weight: bold;
`;

const Subtitle = styled.h3`
    font-family: 'Poppins', sans-serif;
    color: #6f6666;
    font-size: 18px;
    font-weight: bold;
    margin-top: 20px;
    margin-bottom: 10px;
`;

const AddRecipePage = () => {
    const [ingredients, setIngredients] = useState([{ quantity: '', measure: '', ingredient: '', substitute: '', showSubstitute: false }]);
    const [instructions, setInstructions] = useState(['']);
    const [formErrors, setFormErrors] = useState({});
    const [successMessage, setSuccessMessage] = useState('');

    const handleInputChange = (e, index) => {
        const { name, value } = e.target;
        const updatedIngredients = [...ingredients];
        updatedIngredients[index][name] = value;
        setIngredients(updatedIngredients);

        if (name === 'quantity' && !/^\d*$/.test(value)) {
            setFormErrors(prevErrors => ({ ...prevErrors, quantity: 'Only numbers are allowed' }));
        } else if (name === 'ingredient' && !/^[a-zA-Z\s]*$/.test(value)) {
            setFormErrors(prevErrors => ({ ...prevErrors, ingredient: 'Only letters are allowed' }));
        } else {
            setFormErrors(prevErrors => ({ ...prevErrors, [name]: '' }));
        }
    };

    const handleAddIngredient = () => setIngredients([...ingredients, { quantity: '', measure: '', ingredient: '', substitute: '', showSubstitute: false }]);
    const handleDeleteIngredient = index => setIngredients(ingredients.filter((_, i) => i !== index));
    const handleToggleSubstitute = index => setIngredients(ingredients.map((ing, i) => i === index ? { ...ing, showSubstitute: !ing.showSubstitute } : ing));
    const handleAddInstruction = () => setInstructions([...instructions, '']);
    const handleDeleteInstruction = index => setInstructions(instructions.filter((_, i) => i !== index));

    const handleSubmit = (e) => {
        e.preventDefault();
        if (Object.values(formErrors).every(error => !error)) {
            setSuccessMessage('Recipe added successfully!');
        } else {
            setSuccessMessage('');
        }
    };

    return (
        <PageWrapper>
            <Title>Add a New Recipe</Title>
            <Form onSubmit={handleSubmit}>
                <FormGroup>
                    <Label>Recipe Name:</Label>
                    <Input type="text" name="recipeName" onChange={(e) => handleInputChange(e)} error={!!formErrors.recipeName} />
                    <ErrorText show={!!formErrors.recipeName}>{formErrors.recipeName}</ErrorText>
                </FormGroup>

                <FormGroup>
                    <Label>Description:</Label>
                    <FullWidthTextarea placeholder="Add a short description of the recipe" />
                </FormGroup>

                <Subtitle>Ingredients</Subtitle>
                {ingredients.map((ingredient, index) => (
                    <IngredientGroup key={index}>
                        <Input type="text" placeholder="Quantity" name="quantity" value={ingredient.quantity} onChange={(e) => handleInputChange(e, index)} error={!!formErrors.quantity} />
                        <Select name="measure" value={ingredient.measure} onChange={(e) => handleInputChange(e, index)}>
                            <option>Select Measure</option>
                            <option>mg</option>
                            <option>g</option>
                            <option>kg</option>
                            <option>oz</option>
                            <option>lb</option>
                            <option>ml</option>
                            <option>liter</option>
                            <option>cup</option>
                            <option>tbsp</option>
                            <option>tsp</option>
                            <option>fl oz</option>
                            <option>quart</option>
                            <option>pint</option>
                            <option>unit</option>
                            <option>some</option>
                        </Select>
                        <Input type="text" placeholder="Ingredient" name="ingredient" value={ingredient.ingredient} onChange={(e) => handleInputChange(e, index)} error={!!formErrors.ingredient} />
                        {ingredient.showSubstitute && <Input type="text" placeholder="Substitute Ingredient" name="substitute" value={ingredient.substitute} onChange={(e) => handleInputChange(e, index)} />}
                        <ButtonGroup>
                            <Button type="button" className="add" onClick={handleAddIngredient}>+ Add Ingredient</Button>
                            <Button type="button" className="delete" onClick={() => handleDeleteIngredient(index)}>- Delete</Button>
                            <Button type="button" className="substitute" onClick={() => handleToggleSubstitute(index)}>{ingredient.showSubstitute ? '- Delete Substitute' : '+ Substitute'}</Button>
                        </ButtonGroup>
                    </IngredientGroup>
                ))}
                <ErrorText show={!!formErrors.quantity}>{formErrors.quantity}</ErrorText>
                <ErrorText show={!!formErrors.ingredient}>{formErrors.ingredient}</ErrorText>

                <Subtitle>Instructions</Subtitle>
                {instructions.map((instruction, index) => (
                    <FormGroup key={index}>
                        <FullWidthTextarea placeholder={`Step ${index + 1}`} value={instruction} onChange={(e) => setInstructions(instructions.map((ins, i) => i === index ? e.target.value : ins))} />
                        <ButtonGroup>
                            <Button type="button" className="add" onClick={handleAddInstruction}>+ Add Step</Button>
                            <Button type="button" className="delete" onClick={() => handleDeleteInstruction(index)}>- Delete</Button>
                        </ButtonGroup>
                    </FormGroup>
                ))}

                <PhotoFormGroup>
                    <Label>Photo:</Label>
                    <Input type="file" name="photo" />
                </PhotoFormGroup>

                <SubmitButton type="submit">Submit Recipe</SubmitButton>
                {successMessage && <Message type="success">{successMessage}</Message>}
            </Form>
        </PageWrapper>
    );
};

export default AddRecipePage;
