import React, { useState } from 'react';
import styled from 'styled-components';
import { FiInfo } from 'react-icons/fi';

const PageWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 40px;
  min-height: 100vh;
  background-color: #D6C0B3;
  font-family: 'Raleway', sans-serif;
`;

const Form = styled.form`
  width: 100%;
  max-width: 780px;
  padding: 30px;
  border-radius: 10px;
  background: transparent;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1);
  border: 1px solid rgba(200, 200, 200, 0.5);
`;

const FormGroup = styled.div`
  margin-bottom: 20px;
  display: flex;
  flex-direction: column;
  position: relative;
`;

const Title = styled.h2`
  text-align: center;
  margin-bottom: 30px;
  font-family: 'EB Garamond', serif;
  color: #333;
  font-size: 24px;
  font-weight: bold;
`;

const Label = styled.label`
  font-size: 16px;
  color: #333;
  font-family: 'Raleway', sans-serif;
  font-weight: 600;
  display: flex;
  align-items: center;
  margin-bottom: 5px;
`;

const ErrorText = styled.span`
  color: #e74c3c;
  font-size: 14px;
  position: absolute;
  top: 100%;
  left: 0;
  margin-top: 3px;
`;

const InfoIcon = styled(FiInfo)`
  margin-left: 5px;
  color: #888;
  cursor: pointer;
`;

const Input = styled.input`
  padding: 12px 15px;
  border-radius: 5px;
  border: 1px solid rgba(0, 0, 0, 0.2);
  font-size: 16px;
  width: 100%;
  background-color: #fefefe;
  font-family: 'Raleway', sans-serif;
  transition: border-color 0.3s;
  &:focus {
    border-color: #5b9e5d;
    outline: none;
  }
`;

const FullWidthTextarea = styled.textarea`
  padding: 12px 15px;
  border-radius: 5px;
  border: 1px solid rgba(0, 0, 0, 0.2);
  font-size: 16px;
  width: 100%;
  background-color: #fefefe;
  font-family: 'Raleway', sans-serif;
  resize: vertical;
  transition: border-color 0.3s;
  &:focus {
    border-color: #5b9e5d;
    outline: none;
  }
`;

const Row = styled.div`
  display: flex;
  gap: 10px;
  align-items: center;
  justify-content: space-between;
  flex-wrap: wrap;
`;

const Button = styled.button`
  border-radius: 8px;
  padding: 10px 16px;
  font-size: 16px;
  font-family: 'Raleway', sans-serif;
  cursor: pointer;
  color: #FFFFFF;
  border: none;
  transition: background-color 0.3s, transform 0.2s;
  box-shadow: 0px 5px 15px rgba(0, 0, 0, 0.2);
  &:hover {
    transform: translateY(-3px);
  }
`;

const AddRecipePage = () => {
  const [recipeName, setRecipeName] = useState("");
  const [description, setDescription] = useState("");
  const [ingredients, setIngredients] = useState([{ quantity: "", measure: "", name: "", substitute: "", showSubstitute: false }]);
  const [instructions, setInstructions] = useState([""]);
  const [errors, setErrors] = useState({});

  const validateForm = () => {
    let valid = true;
    const newErrors = {};

    if (description.length < 10) {
      newErrors.description = "Description must be at least 10 characters.";
      valid = false;
    }

    ingredients.forEach((ingredient, index) => {
      if (!/^\d+$/.test(ingredient.quantity)) {
        newErrors[`ingredientQuantity${index}`] = "Quantity must be a number.";
        valid = false;
      }
      if (!ingredient.name || /\d/.test(ingredient.name)) {
        newErrors[`ingredientName${index}`] = "Ingredient name must be letters only.";
        valid = false;
      }
    });

    instructions.forEach((step, index) => {
      if (!step) {
        newErrors[`instruction${index}`] = "This step is empty.";
        valid = false;
      }
    });

    setErrors(newErrors);
    return valid;
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      alert("Recipe submitted successfully!");
    }
  };

  return (
    <PageWrapper>
      <Form onSubmit={handleFormSubmit}>
        <Title>Add a New Recipe</Title>

        <FormGroup>
          <Label>Describe your recipe:</Label>
          <FullWidthTextarea
            placeholder="Add a short description of the recipe"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
          {errors.description && <ErrorText>{errors.description}</ErrorText>}
        </FormGroup>

        <FormGroup>
          <Label>What ingredients will you need?</Label>
          {ingredients.map((ingredient, index) => (
            <Row key={index}>
              <Input
                type="text"
                placeholder="Quantity"
                value={ingredient.quantity}
                onChange={(e) => {
                  const newIngredients = [...ingredients];
                  newIngredients[index].quantity = e.target.value;
                  setIngredients(newIngredients);
                }}
              />
              {errors[`ingredientQuantity${index}`] && <ErrorText>{errors[`ingredientQuantity${index}`]}</ErrorText>}
            </Row>
          ))}
        </FormGroup>
        <Button type="submit">Submit Recipe</Button>
      </Form>
    </PageWrapper>
  );
};

export default AddRecipePage;
