import React, { useState } from 'react';
import styled from 'styled-components';
import { FiInfo, FiSearch } from 'react-icons/fi';

const PageWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 40px;
  min-height: 100vh;
  background-color: #f5f5f7;
  font-family: 'Raleway', sans-serif;
`;

const Form = styled.form`
  width: 100%;
  max-width: 600px;
  background-color: #fff;
  padding: 30px;
  border-radius: 10px;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.1);
`;

const FormGroup = styled.div`
  margin-bottom: 20px;
  display: flex;
  flex-direction: column;
  position: relative;
`;

const Label = styled.label`
  font-size: 16px;
  color: #333;
  font-family: 'Poppins', sans-serif;
  font-weight: 600;
  display: flex;
  align-items: center;
`;

const InfoIcon = styled(FiInfo)`
  margin-left: 5px;
  color: #888;
  cursor: pointer;
`;

const InputWrapper = styled.div`
  display: flex;
  align-items: center;
  position: relative;
`;

const InputIcon = styled(FiSearch)`
  position: absolute;
  left: 12px;
  color: #888;
`;

const Input = styled.input`
  padding: 12px 15px 12px 40px;
  border-radius: 5px;
  border: 1px solid #ddd;
  font-size: 16px;
  width: 100%;
  background-color: #f9f9f9;
  transition: border-color 0.3s;
  &:focus {
    border-color: #5b9e5d;
    outline: none;
  }
`;

const FullWidthTextarea = styled.textarea`
  width: 100%;
  padding: 12px 15px;
  border-radius: 5px;
  border: 1px solid #ddd;
  font-size: 16px;
  background-color: #f9f9f9;
  resize: vertical;
  transition: border-color 0.3s;
  &:focus {
    border-color: #5b9e5d;
    outline: none;
  }
`;

const CheckboxGroup = styled.div`
  display: flex;
  flex-direction: column;
  gap: 10px;
`;

const CheckboxLabel = styled.label`
  display: flex;
  align-items: center;
  font-size: 14px;
  color: #555;
`;

const Checkbox = styled.input.attrs({ type: 'checkbox' })`
  margin-right: 10px;
  transform: scale(1.2);
`;

const Button = styled.button`
  border-radius: 5px;
  padding: 12px 20px;
  font-size: 16px;
  font-family: 'Poppins', sans-serif;
  cursor: pointer;
  text-align: center;
  color: #FFFFFF;
  background-color: #5b9e5d;
  border: none;
  transition: background-color 0.3s, transform 0.2s;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1);
  width: 100%;

  &:hover {
    background-color: #4a8c50;
    transform: translateY(-2px);
  }
`;

const Title = styled.h2`
  text-align: center;
  margin-bottom: 30px;
  font-family: 'EB Garamond', serif;
  color: #333;
  font-size: 24px;
  font-weight: bold;
`;

const AddRecipePage = () => {
  const [recipeName, setRecipeName] = useState("");
  const [description, setDescription] = useState("");
  const [ingredients, setIngredients] = useState([{ name: "", checked: false }]);

  const handleAddIngredient = () => setIngredients([...ingredients, { name: "", checked: false }]);
  const handleIngredientChange = (index, field, value) => {
    const newIngredients = [...ingredients];
    newIngredients[index][field] = value;
    setIngredients(newIngredients);
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    const recipeData = { recipeName, description, ingredients };
    console.log("Recipe Data:", recipeData);
  };

  return (
    <PageWrapper>
      <Title>Add a New Recipe</Title>
      <Form onSubmit={handleFormSubmit}>
        <FormGroup>
          <Label htmlFor="recipeName">What would you like to name your recipe? <InfoIcon /></Label>
          <InputWrapper>
            <Input
              type="text"
              id="recipeName"
              placeholder="E.g., Grandma's Apple Pie"
              value={recipeName}
              onChange={(e) => setRecipeName(e.target.value)}
            />
          </InputWrapper>
        </FormGroup>

        <FormGroup>
          <Label htmlFor="description">Describe your recipe:</Label>
          <FullWidthTextarea
            id="description"
            placeholder="Add a short description of the recipe"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
        </FormGroup>

        <FormGroup>
          <Label>What ingredients will you need?</Label>
          <CheckboxGroup>
            {ingredients.map((ingredient, index) => (
              <CheckboxLabel key={index}>
                <Checkbox
                  checked={ingredient.checked}
                  onChange={(e) => handleIngredientChange(index, "checked", e.target.checked)}
                />
                <Input
                  type="text"
                  placeholder="E.g., 2 cups of flour"
                  value={ingredient.name}
                  onChange={(e) => handleIngredientChange(index, "name", e.target.value)}
                />
              </CheckboxLabel>
            ))}
          </CheckboxGroup>
          <Button type="button" onClick={handleAddIngredient} style={{ marginTop: "10px" }}>
            + Add Another Ingredient
          </Button>
        </FormGroup>

        <Button type="submit">Submit Recipe</Button>
      </Form>
    </PageWrapper>
  );
};

export default AddRecipePage;
