import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './css/LoginPage.css';

function LoginPage({ setIsLoggedIn }) {
    const [formData, setFormData] = useState({
        UserName: '',
        Password: '',
    });

    const [errors, setErrors] = useState({});
    const [apiErrorMessage, setApiErrorMessage] = useState('');
    const navigate = useNavigate();

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const validate = () => {
        let inputErrors = {};
        if (!formData.UserName) {
            inputErrors.UserName = 'Username is required';
        }
        if (!formData.Password) {
            inputErrors.Password = 'Password is required';
        }
        return inputErrors;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const inputErrors = validate();

        if (Object.keys(inputErrors).length === 0) {
            try {
                const response = await axios.post('http://localhost:5001/login', formData, {
                    headers: { 'Content-Type': 'application/json' },
                    withCredentials: true,  // Important to send cookies with request
                });

                setIsLoggedIn(true);
                localStorage.setItem('isLoggedIn', 'true');
                setApiErrorMessage('');
                navigate('/');
            } catch (error) {
                if (error.response && error.response.status === 401) {
                    setApiErrorMessage('Invalid username or password.');
                } else {
                    setApiErrorMessage('The username or password is not correct.');
                    console.error('Login error:', error);
                }
            }
        } else {
            setErrors(inputErrors);
        }
    };

    return (
        <div className="login-page">
            {/* Login form implementation */}
        </div>
    );
}

export default LoginPage;
