import React, { useState } from 'react';
import styled from 'styled-components';

const PageWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 40px;
  min-height: 100vh;
  background: linear-gradient(135deg, #e9e4e0 0%, #f3ece8 100%);
  font-family: 'Raleway', sans-serif;
`;

const Form = styled.form`
  width: 100%;
  max-width: 780px;
  padding: 30px;
  border-radius: 15px;
  background: #fff;
  box-shadow: 0px 8px 16px rgba(0, 0, 0, 0.1);
  border: 1px solid rgba(200, 200, 200, 0.3);
`;

const FormGroup = styled.div`
  margin-bottom: 25px;
  display: flex;
  flex-direction: column;
  position: relative;
`;

const Title = styled.h2`
  text-align: center;
  margin-bottom: 30px;
  font-family: 'EB Garamond', serif;
  color: #333;
  font-size: 26px;
  font-weight: bold;
`;

const Label = styled.label`
  font-size: 16px;
  color: #333;
  font-weight: 600;
  display: flex;
  align-items: center;
  margin-bottom: 5px;
`;

const ErrorText = styled.span`
  color: #e74c3c;
  font-size: 14px;
  margin-top: 4px;
  height: 18px;
  visibility: ${({ isVisible }) => (isVisible ? 'visible' : 'hidden')};
`;

const Input = styled.input`
  padding: 12px 15px;
  border-radius: 8px;
  border: 1px solid ${({ isError }) => (isError ? '#e74c3c' : 'rgba(0, 0, 0, 0.2)')};
  font-size: 16px;
  background-color: #fefefe;
  font-family: 'Raleway', sans-serif;
  transition: border-color 0.3s, box-shadow 0.3s;
  box-sizing: border-box;

  &:focus {
    border-color: #5b9e5d;
    box-shadow: 0px 0px 8px rgba(92, 158, 93, 0.3);
    outline: none;
  }
`;

const FullWidthTextarea = styled.textarea`
  padding: 12px 15px;
  border-radius: 8px;
  border: 1px solid rgba(0, 0, 0, 0.2);
  font-size: 16px;
  width: 100%;
  background-color: #fefefe;
  font-family: 'Raleway', sans-serif;
  resize: vertical;
  transition: border-color 0.3s, box-shadow 0.3s;
  box-sizing: border-box;

  &:focus {
    border-color: #5b9e5d;
    box-shadow: 0px 0px 8px rgba(92, 158, 93, 0.3);
    outline: none;
  }
`;

const Select = styled.select`
  padding: 12px 15px;
  border-radius: 8px;
  border: 1px solid rgba(0, 0, 0, 0.2);
  font-size: 16px;
  width: 35%;
  background-color: #fefefe;
  font-family: 'Raleway', sans-serif;
  transition: border-color 0.3s;
  box-sizing: border-box;

  &:focus {
    border-color: #5b9e5d;
    outline: none;
  }
`;

const SubmitButton = styled.button`
  border-radius: 12px;
  padding: 10px 16px;
  font-size: 16px;
  cursor: pointer;
  color: #fff;
  background-color: #8B5E3C;
  border: none;
  width: 100%;
  transition: background-color 0.3s, transform 0.2s;

  &:hover {
    background-color: #70462c;
    transform: translateY(-3px);
  }

  &:disabled {
    background-color: #ccc;
    cursor: not-allowed;
  }
`;

const AddRecipePage = () => {
  const [recipeName, setRecipeName] = useState('');
  const [description, setDescription] = useState('');
  const [finalAmount, setFinalAmount] = useState('');
  const [skillLevel, setSkillLevel] = useState('');
  const [prepTime, setPrepTime] = useState('');
  const [ingredients, setIngredients] = useState([{ quantity: '', measure: '', name: '' }]);
  const [instructions, setInstructions] = useState(['']);
  const [incompleteFields, setIncompleteFields] = useState({});
  const [errors, setErrors] = useState({});

  const isFormValid = () => {
    return (
      recipeName.trim().length >= 3 &&
      description.trim().length >= 10 &&
      finalAmount.trim() !== '' &&
      skillLevel.trim() !== '' &&
      prepTime.trim() !== '' &&
      ingredients.every((ingredient) => ingredient.quantity.trim() !== '' && ingredient.name.trim() !== '') &&
      instructions.every((step) => step.trim().length >= 10)
    );
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();

    const newIncompleteFields = {};
    if (recipeName.trim().length < 3) newIncompleteFields.recipeName = true;
    if (description.trim().length < 10) newIncompleteFields.description = true;
    if (finalAmount.trim() === '') newIncompleteFields.finalAmount = true;
    if (skillLevel.trim() === '') newIncompleteFields.skillLevel = true;
    if (prepTime.trim() === '') newIncompleteFields.prepTime = true;

    ingredients.forEach((ingredient, index) => {
      if (ingredient.quantity.trim() === '') newIncompleteFields[`ingredientQuantity${index}`] = true;
      if (ingredient.name.trim() === '') newIncompleteFields[`ingredientName${index}`] = true;
    });

    instructions.forEach((step, index) => {
      if (step.trim().length < 10) newIncompleteFields[`instruction${index}`] = true;
    });

    setIncompleteFields(newIncompleteFields);

    if (Object.keys(newIncompleteFields).length === 0) {
      alert('Recipe submitted successfully!');
    } else {
      alert('Please fill out all fields correctly.');
    }
  };

  return (
    <PageWrapper>
      <Form onSubmit={handleFormSubmit}>
        <Title>Add a New Recipe</Title>

        <FormGroup>
          <Label>Recipe Name</Label>
          <Input
            type="text"
            value={recipeName}
            onChange={(e) => setRecipeName(e.target.value)}
            isError={incompleteFields.recipeName}
          />
          <ErrorText isVisible={!!incompleteFields.recipeName}>Recipe Name must be at least 3 characters</ErrorText>
        </FormGroup>

        <FormGroup>
          <Label>Description</Label>
          <FullWidthTextarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            isError={incompleteFields.description}
          />
          <ErrorText isVisible={!!incompleteFields.description}>Description must be at least 10 characters</ErrorText>
        </FormGroup>

        <FormGroup>
          <Label>Final Amount</Label>
          <Input
            type="text"
            value={finalAmount}
            onChange={(e) => setFinalAmount(e.target.value)}
            isError={incompleteFields.finalAmount}
          />
          <ErrorText isVisible={!!incompleteFields.finalAmount}>Final Amount is required</ErrorText>
        </FormGroup>

        <FormGroup>
          <Label>Skill Level</Label>
          <Select value={skillLevel} onChange={(e) => setSkillLevel(e.target.value)}>
            <option value="">Select Skill Level</option>
            <option value="Easy">Easy</option>
            <option value="Intermediate">Intermediate</option>
            <option value="Advanced">Advanced</option>
          </Select>
          <ErrorText isVisible={!!incompleteFields.skillLevel}>Skill Level is required</ErrorText>
        </FormGroup>

        <FormGroup>
          <Label>Preparation Time</Label>
          <Input
            type="text"
            value={prepTime}
            onChange={(e) => setPrepTime(e.target.value)}
            isError={incompleteFields.prepTime}
          />
          <ErrorText isVisible={!!incompleteFields.prepTime}>Preparation Time is required</ErrorText>
        </FormGroup>

        <SubmitButton type="submit" disabled={!isFormValid()}>
          Submit Recipe
        </SubmitButton>
      </Form>
    </PageWrapper>
  );
};

export default AddRecipePage;
