import React, { useState } from 'react';
import styled from 'styled-components';
import { FiInfo } from 'react-icons/fi';

const PageWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 40px;
  min-height: 100vh;
  background-color: #D6C0B3;
  font-family: 'Raleway', sans-serif;
`;

const Form = styled.form`
  width: 100%;
  max-width: 750px;
  padding: 30px;
  border-radius: 10px;
  background: linear-gradient(135deg, #EFE2D0, #D6C0B3);
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.1);
`;

const FormGroup = styled.div`
  margin-bottom: 20px;
  display: flex;
  flex-direction: column;
`;

const Title = styled.h2`
  text-align: center;
  margin-bottom: 30px;
  font-family: 'EB Garamond', serif;
  color: #333;
  font-size: 24px;
  font-weight: bold;
`;

const Label = styled.label`
  font-size: 16px;
  color: #333;
  font-family: 'Poppins', sans-serif;
  font-weight: 600;
  display: flex;
  align-items: center;
  margin-bottom: 5px;
`;

const InfoIcon = styled(FiInfo)`
  margin-left: 5px;
  color: #888;
  cursor: pointer;
`;

const Input = styled.input`
  padding: 12px 15px;
  border-radius: 5px;
  border: 1px solid #ddd;
  font-size: 16px;
  width: 100%;
  background-color: #f9f9f9;
  transition: border-color 0.3s;
  &:focus {
    border-color: #5b9e5d;
    outline: none;
  }
`;

const FullWidthTextarea = styled.textarea`
  width: 100%;
  padding: 12px 15px;
  border-radius: 5px;
  border: 1px solid #ddd;
  font-size: 16px;
  background-color: #f9f9f9;
  resize: vertical;
  transition: border-color 0.3s;
  &:focus {
    border-color: #5b9e5d;
    outline: none;
  }
`;

const IngredientGroup = styled.div`
  display: flex;
  gap: 10px;
  align-items: center;
  margin-bottom: 10px;
`;

const Select = styled.select`
  padding: 12px 15px;
  border-radius: 5px;
  border: 1px solid #ddd;
  font-size: 16px;
  width: 100%;
  background-color: #f9f9f9;
  transition: border-color 0.3s;
  &:focus {
    border-color: #5b9e5d;
    outline: none;
  }
`;

const Button = styled.button`
  border-radius: 5px;
  padding: 12px 20px;
  font-size: 16px;
  font-family: 'Poppins', sans-serif;
  cursor: pointer;
  color: #FFFFFF;
  background-color: #5b9e5d;
  border: none;
  transition: background-color 0.3s, transform 0.2s;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1);
  margin-top: 10px;
  width: 100%;

  &:hover {
    background-color: #4a8c50;
    transform: translateY(-2px);
  }
`;

const DeleteButton = styled(Button)`
  background-color: #dc3545;
  width: auto;
  margin-top: 0;
`;

const ErrorText = styled.span`
  color: #dc3545;
  font-size: 14px;
  margin-top: 5px;
`;

const SubmitButton = styled(Button)`
  background-color: #4a8c50;
`;

const AddRecipePage = () => {
  const [recipeName, setRecipeName] = useState("");
  const [description, setDescription] = useState("");
  const [ingredients, setIngredients] = useState([{ quantity: "", measure: "", name: "" }]);
  const [formErrors, setFormErrors] = useState({});

  const handleInputChange = (field, value, index = null) => {
    if (index !== null) {
      const newIngredients = [...ingredients];
      newIngredients[index][field] = value;
      setIngredients(newIngredients);
      if (value.trim()) {
        setFormErrors((prevErrors) => ({ ...prevErrors, [`ingredient${field}${index}`]: "" }));
      }
    } else {
      if (field === "recipeName") setRecipeName(value);
      if (field === "description") setDescription(value);
      if (value.trim()) {
        setFormErrors((prevErrors) => ({ ...prevErrors, [field]: "" }));
      }
    }
  };

  const validateField = () => {
    const errors = {};
    if (!recipeName.trim()) errors.recipeName = "Recipe name is required";
    if (!description.trim()) errors.description = "Description is required";
    ingredients.forEach((ingredient, index) => {
      if (!ingredient.quantity.trim()) errors[`ingredientquantity${index}`] = "Quantity is required";
      if (!ingredient.measure.trim()) errors[`ingredientmeasure${index}`] = "Measure is required";
      if (!ingredient.name.trim()) errors[`ingredientname${index}`] = "Ingredient name is required";
    });
    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    if (validateField()) {
      const recipeData = { recipeName, description, ingredients };
      console.log("Recipe Data:", recipeData);
    }
  };

  return (
    <PageWrapper>
      <Form onSubmit={handleFormSubmit}>
        <Title>Add a New Recipe</Title>
        
        <FormGroup>
          <Label htmlFor="recipeName">What would you like to name your recipe? <InfoIcon /></Label>
          <Input
            type="text"
            id="recipeName"
            placeholder="E.g., Grandma's Apple Pie"
            value={recipeName}
            onChange={(e) => handleInputChange("recipeName", e.target.value)}
          />
          {formErrors.recipeName && <ErrorText>{formErrors.recipeName}</ErrorText>}
        </FormGroup>

        <FormGroup>
          <Label htmlFor="description">Describe your recipe:</Label>
          <FullWidthTextarea
            id="description"
            placeholder="Add a short description of the recipe"
            value={description}
            onChange={(e) => handleInputChange("description", e.target.value)}
          />
          {formErrors.description && <ErrorText>{formErrors.description}</ErrorText>}
        </FormGroup>

        <FormGroup>
          <Label>What ingredients will you need?</Label>
          {ingredients.map((ingredient, index) => (
            <IngredientGroup key={index}>
              <Input
                type="text"
                placeholder="Quantity"
                value={ingredient.quantity}
                onChange={(e) => handleInputChange("quantity", e.target.value, index)}
              />
              {formErrors[`ingredientquantity${index}`] && <ErrorText>{formErrors[`ingredientquantity${index}`]}</ErrorText>}

              <Select
                value={ingredient.measure}
                onChange={(e) => handleInputChange("measure", e.target.value, index)}
              >
                <option value="">Select Measure</option>
                <option value="g">g</option>
                <option value="kg">kg</option>
                <option value="ml">ml</option>
                <option value="cup">Cup</option>
                <option value="tbsp">Tbsp</option>
                <option value="tsp">Tsp</option>
              </Select>
              {formErrors[`ingredientmeasure${index}`] && <ErrorText>{formErrors[`ingredientmeasure${index}`]}</ErrorText>}

              <Input
                type="text"
                placeholder="Ingredient"
                value={ingredient.name}
                onChange={(e) => handleInputChange("name", e.target.value, index)}
              />
              {formErrors[`ingredientname${index}`] && <ErrorText>{formErrors[`ingredientname${index}`]}</ErrorText>}

              <DeleteButton type="button" onClick={() => handleInputChange(index)}>
                - Delete
              </DeleteButton>
            </IngredientGroup>
          ))}
          <Button type="button" onClick={() => setIngredients([...ingredients, { quantity: "", measure: "", name: "" }])}>
            + Add Another Ingredient
          </Button>
        </FormGroup>

        <SubmitButton type="submit">Submit Recipe</SubmitButton>
      </Form>
    </PageWrapper>
  );
};

export default AddRecipePage;
